/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 보고서 조회 완료 후
	if ( sServiceName == "rbs4010-service:searchReportMana" ) {
		sCopyYn = "";
	}

	// 보고서양식 조회 완료 후
	if ( sServiceName == "rbs4010-service:searchReportManaSamp" ) {

		fcSearch();
	}

	 // 보고서 저장 완료 후
	if ( sServiceName == "rbs4010-service:saveReportMana" ) {

		if(sLastSaveRow>-1 && dsReportMana.GetRowTypeNF(sLastSaveRow) == "normal"){
			fcSendMngMsg();
		}

	    fcGetSampReport();

		fcSearch();


	}

	// 첨부파일 조회  완료 후
	if ( sServiceName == "rbs4010-service:searchFileMana" ) {

		//양식불러오기 작업 후  KEY 초기화 
		if(sCopyYn == "Y"){
				for(var i=0; i <= dsUploadFile.GetRowCount();i++){
					dsUploadFile.SetColumn(i,"ATT_FILE_KEY","");
					dsUploadFile.SetColumn(i,"SEQ","");
				}				
		}

	}

	// 보고서담당자 조회 완료 후
	if ( sServiceName == "rbs4010-service:searchReportMng" ) {

		//양식불러오기 작업 후  KEY 초기화 
		if(sCopyYn == "Y"){
				for(var i=0; i <= dsReportMng.GetRowCount();i++){
					dsReportMng.SetColumn(i,"SEQ","");
					dsReportMng.SetColumn(i,"STND_YEAR",sCurYear);
				}				
		}

	}

	// 보고서양식 조회 완료 후(복사) 
	if ( sServiceName == "rbs4010-service:searchReportManaCopy" ) {


		//양식불러오기 작업 후  정보  초기화 
		dsReportMana.SetColumn(dsReportMana.row,"SEQ","");
		dsReportMana.SetColumn(dsReportMana.row,"SUBM_LIMIT_DT","");
		dsReportMana.SetColumn(dsReportMana.row,"REQ_DT",TODAY());
		dsReportMana.SetColumn(dsReportMana.row,"STND_YEAR",sCurYear);
		dsReportMana.SetColumn(dsReportMana.row,"SAMP_YN","");
		dsReportMana.SetColumn(dsReportMana.row,"ATT_FILE_KEY","");

	}
}


/*-------------------------------------------------------------------------------------------------+
>>  양식파일, 보고서 담당자 조회 
+-------------------------------------------------------------------------------------------------*/
function fcDtlSearch(){
	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";


	//첨부파일 조회 
	 sServiceName = "rbs4010-service:searchFileMana";
	 sInDataSet   = "";
	 sOutDataSet  = "dsUploadFile=dsUploadFile ";

	 //조회조건 : 
	 fcd_AddParam("ATT_FILE_KEY", dsReportMana.GetColumn(dsReportMana.row, "ATT_FILE_KEY"  ));

	 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);



	 //담당자 조회 
	 sServiceName = "rbs4010-service:searchReportMng";
	 sInDataSet   = "";
	 sOutDataSet  = "dsReportMng=dsReportMng ";

	 //조회조건 : 
	 fcd_AddParam("DEPT_CD", dsReportMana.GetColumn(dsReportMana.row, "DEPT_CD"  ));
	 fcd_AddParam("STND_YEAR", dsReportMana.GetColumn(dsReportMana.row, "STND_YEAR"  ));
	 fcd_AddParam("SEQ", dsReportMana.GetColumn(dsReportMana.row, "SEQ"  ));

	 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}



/*-------------------------------------------------------------------------------------------------+
>>  양식콤보 조회 
+-------------------------------------------------------------------------------------------------*/

function fcGetSampReport(){

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	 sServiceName = "rbs4010-service:searchReportManaSamp";
	 sInDataSet   = "";
	 sOutDataSet  = "dsReportManaSamp=dsReportManaSamp ";

	 //조회조건 : 
	 fcd_AddParam("DEPT_CD", sDeptCd);
	 fcd_AddParam("SAMP_YN", "Y");
	 fcd_AddParam("REQ_STR_DT", calReqStrDt.Value);
	 fcd_AddParam("REQ_END_DT", calReqEndDt.Value);
	 fcd_AddParam("REPORT_NM", edSearchReportNm.Text);

	 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}


/*-------------------------------------------------------------------------------------------------+
>>  복사대상 양식  조회 
+-------------------------------------------------------------------------------------------------*/
function fcSearchReportCopy(copyRow){

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	 sServiceName = "rbs4010-service:searchReportManaCopy";
	 sInDataSet   = "";
	 sOutDataSet  = "dsReportMana=dsReportMana ";

	 //조회조건 : 
	 fcd_AddParam("DEPT_CD", dsReportManaSamp.GetColumn(copyRow,"DEPT_CD"));
	 fcd_AddParam("STND_YEAR", dsReportManaSamp.GetColumn(copyRow,"STND_YEAR"));
	 fcd_AddParam("SEQ", dsReportManaSamp.GetColumn(copyRow,"SEQ"));

     //양식불러오기 작업 상태 변경 
     sCopyYn = "Y";

	 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}



function fcSendMngMsg(){

	var sSendUser = gdsUser.GetColumn(0,"USER_ID");
	var sRecvUser = "";
	var sTitle	  = "보고서 제출 요청("+dsReportMana.GetColumn(sLastSaveRow,"REPORT_NM")+")" ;
	var sDesc	  = "보고서명 : " +dsReportMana.GetColumn(sLastSaveRow,"REPORT_NM") ;

	if(!fnc_Message(SNIS_RBM_0004,"Y","")){ return;}

	/*
	for(var i = 0; i <dsReportMng.GetRowCount();i++){
		fcd_SendMessage(sSendUser		// 송신자 사번 
		, dsReportMng.GetColumn(i,"MNG_ID")					// 수신자 사번 
		, sTitle	    			// 제목
		, "RBS4020" 				// 수신자 번호
		,  sDesc					// 수신자명
		, "comSendMsg"
		);		

	}	
	*/
	for(var i = 0; i <dsReportMng.GetRowCount();i++){
		if (i>0) {
			sRecvUser += ",";
		}
		sRecvUser += dsReportMng.GetColumn(i,"MNG_ID");	//수신자 사번
	}	

	fcd_SendMessage(
	  sSendUser					// 송신자 사번 
	, sRecvUser					// 수신자 사번 
	, sTitle	    			// 제목
	, "RBS4020" 				// 수신자 번호
	,  sDesc					// 내용
	, "comSendMsg"
	);		
}

