/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
    var nRow = 0;
	if (obj.GetForm().id == "specBtnRow") {
		nRow = dsWinHis.AddRow(); // Record Add
		if (Length(cboGameCd.Value) == 3) {
			dsWinHis.SetColumn(nRow, "GAME_CD", cboGameCd.Value);
		}
	}		
}


