/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {

	if (obj.GetForm().id == "specBtnRow") {
		var dCnt = 0;
		for(var i = dsWinHis.GetRowCount() -1; i >= 0 ; i--) {
			if(dsWinHis.GetColumn(i, "chk") == 1)
				dsWinHis.DeleteRow(i);
		}		
	}
}


