/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	if ( sServiceName == "common-service:searchCode" ) {
		fcGamefilter();
		cboGameCd.Value = GetReg("GAME_CD");

	}	else if ( sServiceName == "rbs5110-service:searchWinHis" ) {
		//
	}	else if ( sServiceName == "rbs5030-service:searchContestList" ) {
		//Trace(dsContest.savexml());
	}	else if ( sServiceName == "rbs5110-service:saveWinHis" ) {	// 저장후
		//fcSearch();  // 다시 조회한다.
	}			

}



