/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/


function fcOnRowPosChanged(obj,nOldRow,nRow)
{
	if (obj.id == "dsRacerList") {

	} else if (obj.id == "dsWinHis") {
		fcCheckVisible(nRow);
	} else if (obj.id == "dsContest") {
		fcCheckVisibleAll(nRow);
		calWinDy.Value = dsContest.GetColumn(nRow, "STR_DY");
	}
}

function fcOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	switch (obj.id) {
		case "cboGameCd":
			setReg("GAME_CD", cboGameCd.Value);
			fcSearchRacer();
			fcSearchContest(obj);
			fcSearch();
			break;
	}
}


function fcOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (obj.id == "dsWinHis") {
		if (strColumnID == "GAME_CD") {
			fcCheckVisible(nRow);
		}
	} else if (obj.id == "dsContest") {
		if (dsContest.GetColumn(nRow, "CHK") == "1") {
			for (var icnt=0;icnt<dsContest.rowcount();icnt++) {
				if (dsContest.GetColumn(icnt, "CHK") == "1" && icnt <> nRow) {
					dsContest.SetColumn(icnt, "CHK", "0");
				}
			}
		}
	}
}

function fcCheckVisibleAll(nRow)
{
	if (dsContest.GetColumn(nRow, "GAME_CD") == "001" &&
	    dsContest.GetColumn(nRow, "INTL_TYPE") == "0") {
		stMrtnRaceTypeAll.Visible = true;
		cboMrtnRaceTypeAll.Visible = true;
	} else {
		stMrtnRaceTypeAll.Visible = false;
		cboMrtnRaceTypeAll.Visible = false;
	}
}

function fcCheckVisible(nRow)
{
	if (dsWinHis.GetColumn(nRow, "GAME_CD") == "001" &&
	    dsWinHis.GetColumn(nRow, "INTL_TYPE") == "0") {
		stMrtnRaceType.Visible = true;
		cboMrtnRaceType.Visible = true;
	} else {
		stMrtnRaceType.Visible = false;
		cboMrtnRaceType.Visible = false;
	}
}

function fcBatchInput(obj)
{
	if (fnc_isnull(cboRank.Value)) {
		fnc_message("순위를 선택하세요!");
		return;
	}
	if (fnc_isnull(calWinDy.Value)) {
		fnc_message("입상일자를 선택하세요!");
		return;
	}
	var chkRow = dsContest.FindRow("CHK", "1");
	if (chkRow <> -1) {
		if (dsContest.GetColumn(chkRow, "GAME_CD") == "001" &&
			dsContest.GetColumn(chkRow, "INTL_TYPE") == "0") {
			if (fnc_IsNull(cboMrtnRaceTypeAll.Value)) {
				fnc_message("국내 마라톤 경기인 경우 반드시 코스종류를 선택하세요..!");
				return;
			}
		}
	}
	if (toNumber(mePayAmt) > 0) {
		if (calPayDy.Value == "") {
			fnc_Message("포상금 지급시 반드시 지급일자를 입력해야 합니다.", "N");
			calPayDy.SetFocus();
			return;
		}
	}

	var nRow = 0;
	for (var icnt=0;icnt<dsRacerList.RowCount();icnt++) {
		if (dsRacerList.GetColumn(icnt, "CHK") != "1") continue;
		for (var jcnt=0;jcnt<dsContest.rowcount();jcnt++) {
			if (dsContest.GetColumn(jcnt, "CHK") != "1") continue;
			nRow = dsWinHis.AddRow();
			dsWinHis.SetColumn(nRow, "RACER_ID", 	dsRacerList.GetColumn(icnt, "RACER_ID"));
			dsWinHis.SetColumn(nRow, "RACER_NM", 	dsRacerList.GetColumn(icnt, "RACER_NM"));
			dsWinHis.SetColumn(nRow, "GAME_CD",  	dsContest.GetColumn(jcnt, "GAME_CD"));
			dsWinHis.SetColumn(nRow, "CONTEST_NM", 	dsContest.GetColumn(jcnt, "CONTEST_NM"));
			dsWinHis.SetColumn(nRow, "CONTEST_SEQ", dsContest.GetColumn(jcnt, "CONTEST_SEQ"));
			dsWinHis.SetColumn(nRow, "INTL_TYPE", 	dsContest.GetColumn(jcnt, "INTL_TYPE"));
			dsWinHis.SetColumn(nRow, "RANK", 		cboRank.Value);
			dsWinHis.SetColumn(nRow, "PRV_GRP_TYPE",cboPrvGrpType.Value);
			dsWinHis.SetColumn(nRow, "WIN_DY", 		calWinDy.Value);
			dsWinHis.SetColumn(nRow, "ENTY_NM", 	edEntyNm.text);			
			dsWinHis.SetColumn(nRow, "MRTN_RACE_TYPE", cboMrtnRaceTypeAll.value);
			dsWinHis.SetColumn(nRow, "RMK", 		txRmk.Text);
			dsWinHis.SetColumn(nRow, "PAY_DY", 		calPayDy.Value);
			dsWinHis.SetColumn(nRow, "PAY_AMT", 	mePayAmt.Value);	
			fcCheckVisible(nRow);
			break;
		}
	}
	cboRank.Value = "";
	edEntyNm.text = "";
	cboMrtnRaceTypeAll.value = "";
	txRmk.Text = "";
	calPayDy.Value = "";
	mePayAmt.Text = "";
}

/*-------------------------------------------------------------------------------------------------+
>>  팀별로 관리하는 종목만 조회되게 권한 관리
+-------------------------------------------------------------------------------------------------*/
function fcGamefilter(obj)
{
	if (IsExistVar("sApro", true)) {
		if (sApro != "T" && sMnAuthRmk != "") {
			cboGameCd.Value = sMnAuthRmk;
			//cboGameCd.Enable = false;

			var filterStr = "CD='" + sMnAuthRmk + "'";
			dsGameCd.Filter(filterStr);

			return false;
		}
    }
    return true;
}
