/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  
    //sBUTTONLIST   = 'FT'+sInpt+'FTFT';     //초기화,조회,저장,삭제,엑셀,인쇄,닫기
    sBUTTONLIST   = 'FT'+sInpt+'FTFT'; 

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

    calStrDt.Value = mid(today(),0,4)+"0101";
    calEndDt.Value = AddDate(today(), 30);

    //공통코드 조회
	fcd_GetComboDs(dsInComCd);

	// 화면 열리자마자 조회
	fcSearchRacer(obj);			//선수명단
	fcSearchRwrdRule(obj);		// 포상기준

	fcSearch(obj);	

}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "dsRwrdHis";
	if (   !fnc_FormUnloadCheck(sDataset  , sFormId) ) return false;
}

