/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
    var nRow = 0;
	if (obj.GetForm().id == "specBtnRow") {
		nRow = dsRwrdHis.AddRow(); // Record Add
		dsRwrdHis.SetColumn(nRow, "PAY_DY", today());

		if (chkRacerOnly.value == "1") {
			if (dsRacerList.RowCount() > 0 && dsRacerList.row >= 0) {
				dsRwrdHis.SetColumn(nRow, "RACER_ID", dsRacerList.GetColumn(dsRacerList.Row, "RACER_ID"));
				dsRwrdHis.SetColumn(nRow, "RACER_NM", dsRacerList.GetColumn(dsRacerList.Row, "RACER_NM"));
			}
		}
	}		
}


