>>  행삽입 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInsRow(obj) {
    // 여기서는 사용하지 않는다.	
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {

	if (obj.GetForm().id == "specBtnRow") {
		var dCnt = 0;
		for(var i = dsRwrdHis.GetRowCount() -1; i >= 0 ; i--) {
			if(dsRwrdHis.GetColumn(i, "chk") == 1)
				dsRwrdHis.DeleteRow(i);
		}		
	}
}


/***************************************************************************************************
*                          CALL BACK 처리 부분                                                     *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	if ( sServiceName == "common-service:searchCode" ) {
		if (fcGamefilter() == true) {	
			// 공통코드 조회 완료 후
			var nRow = dsGameCd.InsertRow(0);
			dsGameCd.SetColumn(nRow, "CD", "%");
			dsGameCd.SetColumn(nRow, "CD_NM", "전체");
			cboGameCd.Value = GetReg("GAME_CD");
		}
	}			

	if ( sServiceName == "rbs5130-service:searchRwrdHis" ) {
		//
	}			

	// 저장 완료 후
	if ( sServiceName == "rbs5130-service:saveRwrdHis" ) {
		//fcSearch();  // 다시 조회한다.
	}			

}



