/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    fcInit(obj);  // 그리드 초기화

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	// 변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsRwrdHis)) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 필수항목체크
	oDataSet          = dsRwrdHis;	
	sMandatoryColID   = "GAME_CD|RACER_ID|PAY_AMT|PAY_DY";
	sMandatoryColName = "종목|선수|포상금액|지급일자";	
	oGrid             = grdRwrdHis;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
		// 필수항목 누락		
		return false;
	}

	return true;
}


