/*-------------------------------------------------------------------------------------------------+
>>  대회 팝업 열기  
+-------------------------------------------------------------------------------------------------*/
function fcWinHisDialog(obj)
{
	if (dsRwrdHis.rowcount() == 0) {
		fnc_message("한줄을 추가후 작업하세요.", "N");
		return;
	}
	var nRow = dsRwrdHis.Row;
	var sGameCd = cboGameCd.Value;
	if (sGameCd == "%") {
		sGameCd = dsRwrdHis.GetColumn(nRow, "GAME_CD");
	}	
	var args  = "pGameCd=" + quote(sGameCd) + " MULTY=" + quote("N");	
	var bRtn = Dialog("frm_rbs::RBS5110P.xml", args, 334, 443, "");

	if(bRtn) {
		dsRwrdHis.SetColumn(nRow, "GAME_CD", 			G_ds_return.GetColumn(0, "GAME_CD"));		
		dsRwrdHis.SetColumn(nRow, "GAME_NM", 			G_ds_return.GetColumn(0, "GAME_NM"));		
		dsRwrdHis.SetColumn(nRow, "RACER_ID", 			G_ds_return.GetColumn(0, "RACER_ID"));		
		dsRwrdHis.SetColumn(nRow, "RACER_NM", 			G_ds_return.GetColumn(0, "RACER_NM"));		
		dsRwrdHis.SetColumn(nRow, "RANK", 				G_ds_return.GetColumn(0, "RANK"));		
		dsRwrdHis.SetColumn(nRow, "INTL_TYPE", 			G_ds_return.GetColumn(0, "INTL_TYPE"));		
		dsRwrdHis.SetColumn(nRow, "CONTEST_SEQ", 		G_ds_return.GetColumn(0, "CONTEST_SEQ"));		
		dsRwrdHis.SetColumn(nRow, "INTL_TYPE", 			G_ds_return.GetColumn(0, "INTL_TYPE"));		
		dsRwrdHis.SetColumn(nRow, "CONTEST_TYPE_SEQ", 	G_ds_return.GetColumn(0, "CONTEST_TYPE_SEQ"));		
		dsRwrdHis.SetColumn(nRow, "CONTEST_NM", 		G_ds_return.GetColumn(0, "CONTEST_NM"));		
		dsRwrdHis.SetColumn(nRow, "WIN_HIS_SEQ", 		G_ds_return.GetColumn(0, "WIN_HIS_SEQ"));		
		dsRwrdHis.SetColumn(nRow, "MRTN_RACE_TYPE", 	G_ds_return.GetColumn(0, "MRTN_RACE_TYPE"));		
		dsRwrdHis.SetColumn(nRow, "PRV_GRP_TYPE", 	 	G_ds_return.GetColumn(0, "PRV_GRP_TYPE"));		
		if (fnc_IsNull(dsRwrdHis.GetColumn(nRow, "WIN_DY"))) {
			dsRwrdHis.SetColumn(nRow, "WIN_DY", 				G_ds_return.GetColumn(0, "STR_DY"));		
		}

		//포상금 계산
		fcCalcRwrd(obj, nRow);
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  선수검색 팝업 열기  
+-------------------------------------------------------------------------------------------------*/
function fcRacerDialog(obj)
{
	var sGameCd = "";
	if (dsRwrdHis.rowcount() == 0) {
		fnc_message("한줄을 추가후 작업하세요.", "N");
		return;
	}
	var sGameCd = cboGameCd.Value;
	var nRow = dsRwrdHis.Row;
	if (sGameCd == "%") {
		sGameCd = dsRwrdHis.GetColumn(nRow, "GAME_CD");
	}
	var args  = "pGameCd=" + quote(sGameCd) + " MULTY=" + quote("N");	
	var bRtn = Dialog("frm_rbs::RBS5010P.xml", args, 334, 443, "");

	if(bRtn) {
		dsRwrdHis.SetColumn(nRow,  "GAME_CD", 	G_ds_return.GetColumn(0, "GAME_CD"));		
		dsRwrdHis.SetColumn(nRow,  "GAME_NM", 	G_ds_return.GetColumn(0, "GAME_NM"));		
		dsRwrdHis.SetColumn(nRow, "RACER_ID", 	G_ds_return.GetColumn(0, "RACER_ID"));		
		dsRwrdHis.SetColumn(nRow, "RACER_NM", 	G_ds_return.GetColumn(0, "RACER_NM"));		
	}
}
function fcOnRowPosChanged(obj,nOldRow,nRow)
{
	if (obj.id == "dsRwrdHis") {
	} else if (obj.id == "dsRacerList") {
		if (chkRacerOnly.Value == 1)	 fcSearch(obj);
	}
}

function fcOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	switch (obj.id) {
		case "cboGameCd":
			fcSearchRacer();
			chkRacerOnly.Value = "0";
			setReg("GAME_CD", cboGameCd.Value);
			fcSearch();
			break;
	}
}


