/*-------------------------------------------------------------------------------------------------+
>>  포상금 자동계산
+-------------------------------------------------------------------------------------------------*/
function fcCalcRwrd(obj, nRow)
{
	var gameCd = dsRwrdHis.GetColumn(nRow, "GAME_CD"); 
	var contestTypeSeq = dsRwrdHis.GetColumn(nRow, "CONTEST_TYPE_SEQ"); 
	var rank = dsRwrdHis.GetColumn(nRow, "RANK"); 
	var MrtnRaceType = dsRwrdHis.GetColumn(nRow, "MRTN_RACE_TYPE");
	var PrvGrpType = dsRwrdHis.GetColumn(nRow, "PRV_GRP_TYPE");

	var searchStr = "GAME_CD="+quote(gameCd) + " && CONTEST_TYPE_SEQ=" + quote(contestTypeSeq) +
	                " && RANK=" + quote(rank) + " && MRTN_RACE_TYPE=" + quote(MrtnRaceType) +
	                " && PRV_GRP_TYPE=" + quote(PrvGrpType);
	var rRow = dsRwrdRule.SearchRow(searchStr);
	var rwrdAmt = dsRwrdRule.GetColumn(rRow, "RWRD_AMT_1");
	if (fnc_isnull(dsRwrdHis.GetColumn(nRow, "PAY_AMT"))) {
		dsRwrdHis.SetColumn(nRow, "PAY_AMT", rwrdAmt);
	}
}


/*-------------------------------------------------------------------------------------------------+
>>  팀별로 관리하는 종목만 조회되게 권한 관리
+-------------------------------------------------------------------------------------------------*/
function fcGamefilter(obj)
{
	if (IsExistVar("sApro", true)) {
		if (sApro != "T" && sMnAuthRmk != "") {
			cboGameCd.Value = sMnAuthRmk;
			//cboGameCd.Enable = false;

			var filterStr = "CD='" + sMnAuthRmk + "'";
			dsGameCd.Filter(filterStr);
			return false;
		}
    }
    return true;
}
