}

/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	//Return Key Press時 Next Component focus
	if ( nChar == ENTER_KEY ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}

	//Control Key가 눌려져 있다면 단축키 처리
	//if (bControl) fnc_HotKey(this, nChar);
}

/***************************************************************************************************
*                                      공통 버튼 Event 처리 부분                                   *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
	if (tab0.TabIndex == 0) 
		dsApplyList.ClearData();
	else 
		dsWinnerList.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
	fcInit(obj);
	return true;
}

function fcSearch(obj)
{
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";     
	if (tab0.TabIndex == 0) {
		sServiceName = "rbs8020-service:searchApplyList";
		sInDataSet   = "";
		sOutDataSet  = "dsApplyList=dsApplyList";    

    } else {
		sServiceName = "rbs8020-service:searchWinnerList";
		sInDataSet   = "";
		sOutDataSet  = "dsWinnerList=dsWinnerList";     
    }

	fcRsvYearValue();	

	fcd_AddParam("RSV_YEAR"  , sRsvYear);	
	fcd_AddParam("RSV_TMS"   , sRsvTms);	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
	stWinRslt.text = "";
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSaveCheck(obj)
{ 	
	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsApplyList)) {
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	} else 	
		return true;
}

function fcSave(obj)
{

	if ( !fcSaveCheck() ) return;

	var sServiceName = "rbs8020-service:saveDrawRslt";	// 추첨결과 저장
	var sInDataSet   = "dsApplyList=dsApplyList:u ";
	var sOutDataSet  = "";	
	fcRsvYearValue();

	fcd_AddParam("RSV_YEAR"  , sRsvYear);	
	fcd_AddParam("RSV_TMS"   , sRsvTms);	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {
    var sExcelList = "tab0.tab1.grdList:신청자 명단";
    if (tab0.TabIndex == 1) sExcelList = "tab0.tab2.grdList:당첨자 명단";
    fcd_OpenExcelListExport(sExcelList);
}


/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
