function fcGridSort(obj,nCell,nX,nY) {
	//특정 Head 클릭 시 해당 항목으로 Sort(만약 특정 컬럼을 누를때만 소트하고자 한다면 nCell로 조건)
	if ( nCell != 0 ) {
		fnc_GridSort(obj, object(obj.BindDataset), nCell);
	}
}


