/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	if (sServiceName == "common-service:searchCode") {
		fcSearch();
	}
	// 조회 완료 후
	if ( sServiceName == "rbs8020-service:searchApplyList") {

		if (dsApplyList.FindRow("WIN_YN", "Y") > -1) {
			fcDisplayDrawRslt();
		}

	}			
	if ( sServiceName == "rbs8020-service:searchWinnerList") {

		if(dsWinnerList.rowcount == 0) return;


	}				

	// 저장 완료 후
	if ( sServiceName == "rbs8020-service:saveDrawRslt" ) {

	}	

}

function fcRsvYearValue(obj)
{
	/* 콤보박스에서 값 가져오기 */
    var nCbValue =cbRsvYear.Value;
	var nCbRow = dsRsvYear.SearchRow("CD=="+nCbValue);
	if (nCbRow == -1) {
		sRsvYear = "";
		sRsvTms  = "";
	} else {	
		sRsvYear = dsRsvYear.GetColumn(nCbRow,"RSV_YEAR");
		sRsvTms  = dsRsvYear.GetColumn(nCbRow,"RSV_TMS");
	}	
}

