{
	switch(obj.id) {
		case "dsApplyList" :
			break;
	}
}

//버튼 Click
function fcBtnOnClick(obj) {
	switch( obj.id ) {
		case "btnDraw" :	//휴양소 추첨
			fcDraw();
			break;
		case "btnWeight" : //가중치 적용
			fcSetWeight();
			break;
		case "btnSendSMS":	//문자메세지 발송
			fcSendSMS();
			break;
	}
}

/*************************************************************************************************
                        추첨 처리
**************************************************************************************************/

var arrRestId;		// 기간별 휴양소 배열
var arrRestPool;

function fcDraw()
{
	var nDeptCnt	=0;
	var nItmCnt 	=0;
	var nCnt		=0;
	nGrpSeq = 1;

	if( dsApplyList.rowcount == 0) return;
	if (dsApplyList.FindRow("WIN_YN", "Y") > -1) {
		if (!fnc_message("이미 당첨처리가 되었었습니다. 다시 추첨하시겠습니까?", "Y"))
			return;
	}

	stWinRslt.text = "";
	dsApplyList.fireEvent = false;	

	// 휴양소ID/연번 추출
	arrRestId = array(-1);	
	for(var i=0; i<dsApplyList.rowcount;i++){
		dsApplyList.SetColumn(i, "WIN_YN", "N");	//초기화
		var RestId = dsApplyList.GetColumn(i,"RST_ID")+dsApplyList.GetColumn(i,"RST_SEQ");
		if( !fcExitItemCd(arrRestId, RestId) ){			
			// 존재하지 않으면...
			arrRestId[nCnt] = RestId;	//20130101+01
			nCnt++;
		}
	}	
	Trace("arrRestId List====");
	for (var i=0;i<Length(arrRestId);i++) {
		Trace(arrRestId[i]);
	}

	// 추첨추출
	var nWinMaxNo=0;
	var nMenCnt=0;
	var n,x;
	var nMod, nVal;
	var filterstr = "";
	var UserId = "";
	var WinNo = 0;
	var nWval = 0;
	var nTotWval = 0;

	dsApplyList.Sort("RST_ID,RST_SEQ");		// 휴양소id, 휴양소연번

	for(var i=0;i<length(arrRestId);i++) {
		filterstr = "RST_ID="+quote(substr(arrRestId[i],0,8))+"&&RST_SEQ="+quote(substr(arrRestId[i],8,2));
		dsApplyList.Filter(filterstr);
		nWinMaxNo = toNumber(dsApplyList.GetColumn(0, "RSV_MAX_NUM"));
		nMenCnt = dsApplyList.rowcount;	
		if( nMenCnt <= nWinMaxNo){
			for(var k=0;k<dsApplyList.rowcount;k++){
				if (dsApplyList.GetColumn(k, "APPLY_CNT") > 0) {				
					dsApplyList.SetColumn(k, "WIN_YN", "Y");	// 가능인원이내인 경우 전부 당첨처리			
				}			
			}
		}else{
			WinCnt = 0;
			while (nWinMaxNo>WinCnt) {

				// 추첨용 POOL 생성
				nWval    = 0;		//가중치
				nTotWval = 0;		//총가중치
				arrRestPool = array(-1);

				for(var k=0;k<dsApplyList.rowcount;k++){
					if (dsApplyList.GetColumn(k, "WIN_YN")!="Y") {
						nWval = dsApplyList.GetColumn(k, "WEIGHT_VAL");
						UserId = dsApplyList.GetColumn(k, "USER_ID");
						for (var l=0;l<nWval;l++) arrRestPool[nTotWval++]=UserId;
					}
				}
				WinNo = random(0, nTotWval -1);
				WinUserId = arrRestPool[WinNo];	//당첨된 사번
				var WinRow = dsApplyList.FindRow("USER_ID", WinuserId);
				dsApplyList.SetColumn(WinRow, "WIN_YN", "Y");
				WinCnt ++;
			}
		}
		dsApplyList.UnFilter();
	}
	dsApplyList.fireEvent = true;

	fcDisplayDrawRslt();
}

function fcDisplayDrawRslt()
{
	var WinRslt[2];	
	for (var i=0;i<dsApplyList.rowcount();i++) {
		if (dsApplyList.GetColumn(i, "WIN_YN") == "Y") {
			if (dsApplyList.GetColumn(i, "WK_TERM_TYPE") == "001") {
				 WinRslt[0] ++;
			} else {
				 WinRslt[1] ++;
			}			
		}
	}
	stWinRslt.text = "추첨결과 >>> 10년 이상 : " +WinRslt[0]+"명, 10년 미만 : "+WinRslt[1]+"명";		
}

// 휴양소리스트가 이미 존재하는지 체크(true/false)
function fcExitItemCd(arr,strItmCd)
{
	for(var i=0;i<length(arr);i++){
		if( arr[i] == strItmCd ){
			return true;
		}
	}
	return false;
}

function fcSetWeight()
{
	var nWeightValue = edWeightValue.Text;
	for (var iCnt=0;iCnt<dsApplyList.rowcount();iCnt++) {
		if (dsApplyList.GetColumn(iCnt, "WK_TERM_TYPE") == "001") {
			dsApplyList.SetColumn(iCnt, "WEIGHT_VAL", ToNumber(nWeightValue));
		}	
	}
}


function fctabOnChanged(obj,nOldIndex,nNewindex)
{
	var bDrawButton;
	if (obj.id == "Tab0") {
		bDrawButton = false;
		if (nNewindex == 0) {
			bDrawButton = true;
		} else {
			bDrawButton = false;
		}
		edWeightValue.Visible = false; //bDrawButton;
		btnWeight.Visible = false; //bDrawButton;
		btnDraw.Visible = bDrawButton;
		stWinRslt.Visible = bDrawButton;
		btnSendSMS.Visible = !bDrawButton;
	}
}

function fcSendSMS()
{
	var sSendPhoneNumber 	= gdsUser.GetColumn(0,"HP_NO");	 //송신자이동전화번호 
	var sSendUserId 	 	= gdsUser.GetColumn(0,"USER_ID");//송신자 사번
	var sSendUserName 		= gdsUser.GetColumn(0,"USER_NM");//송신자명 
	var sReceivePhoneNumber = "";							 //수신자이동전화번호 , 여러명인경우 ; 로 구분 
	var sReceiveName 		= "";							 //수신자이동전화번호 , 여러명인경우 ; 로 구분 
	var sParams				= "";
	var sReceiveId          = "";
	var sTitle              = "하계휴양소 당첨 안내";
	var sPermTempGbn        = "002";						// 001:다시 전체 일용직 조회

	for(var i = 0; i <dsWinnerList.GetRowCount();i++){
		if (fnc_IsNotNull(dsWinnerList.GetColumn(i,"HP_NO")))  {
			sReceivePhoneNumber +=  dsWinnerList.GetColumn(i,"HP_NO")+";";
			sReceiveName        +=  dsWinnerList.GetColumn(i,"USER_NM")+";";
			sReceiveId          +=  dsWinnerList.GetColumn(i,"USER_ID")+";";
		}
	}

	sReceivePhoneNumber = substr(sReceivePhoneNumber,0,LENGTH(sReceivePhoneNumber)-1);
	sReceiveName        = substr(sReceiveName,0,LENGTH(sReceiveName)-1);
	sReceiveId          = substr(sReceiveId,0,LENGTH(sReceiveId)-1);

	sParams = "sSendPhoneNumber="+quote(sSendPhoneNumber);
	sParams += " sSendUserId="+quote(sSendUserId);
	sParams += " sSendUserName="+quote(sSendUserName);
	sParams += " sReceivePhoneNumber="+quote(sReceivePhoneNumber);
	sParams += " sReceiveName="+quote(sReceiveName);
	sParams += " sReceiveId="+quote(sReceiveId);
	sParams += " sTitle="+quote(sTitle);
	sParams += " sRecvrUserType="+quote(sPermTempGbn);

	nHandle = Open("common::comSendSms.xml", sParams, 240, 328, "Title=false");


	break;
}
