function fcHelpDialoge(obj,nX,nY) {

}



/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 일자 조회 후
	if ( sServiceName == "rem4090-service:searchCronDate" ) {

	}

	// 전송상태 조회 후
	if ( sServiceName == "rem4090-service:searchCronStatus" ) {
		for( var i=0; i < dsCronStatus.GetRowCount(); i++) {
			if(dsCronStatus.GetColumn(i,"TRANS_STATUS") == "Y")
			{

			}
		}
	}

	// 입장현황 수동배치 완료 후
	if ( sServiceName == "rem4090-service:SaveCronData" ) {
		dsCronStatus.ClearData();
		fcSearch();
	}

}

function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{

	switch ( obj.id ) {
		case "cboCommCd":

			fcSearch();

			break;

	}
}

function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	switch ( obj.id ) {

		case "dsBrncCd":

			dsBrncCd.InsertRow(0);
			dsBrncCd.SetColumn(0, "CD_NM", "전체");
			cboCommCd.index = 0;

			break;	
	}
}

