/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {

	if (tab0.TabIndex == 0)	dsGoodSum.ClearData();
	else                    dsIfisList.ClearData();

}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}



/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	if ( !fcSearchCheck(obj) ) return;
	if (tab0.TabIndex == 0) {
		var YrItemYn = dsCommNo.GetColumn(cboSearchBrncCd.Index, "CD_TERM3");
		if (Mid(YrItemYn,0,1) == "1") {
			stYearItemInfo.visible = true;
			return ;	// 단가계약관리 지점에서 일반 품목 조회 불가
		} else {
			stYearItemInfo.visible = false;
		}


		sServiceName = "rem5060-service:searchGoodSum";
		sInDataSet   = "";
		sOutDataSet  = "dsGoodSum=dsGoodSum ";

		//조회조건 : 
		fcd_AddParam("BRNC_CD", cboSearchBrncCd.Value);
		fcd_AddParam("STR_DT", calStrDt.Value);
		fcd_AddParam("END_DT", calEndDt.Value);
		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

	} else {
		var sIdx    = dsBrncCd.FindRow("CD", cboSearchBrncCd.Value);		
		var sDeptCd = dsBrncCd.GetColumn(sIdx, "CD_TERM2");

		sServiceName = "rem5060-service:searchIfisOutDecise";
		sInDataSet   = "";
		sOutDataSet  = "dsIfisList=dsIfisList ";		

		fcd_AddParam("DEPT_CD", substr(sDeptCd, 2, 6));    // 부서코드 
		fcd_AddParam("STR_DT" , calStrDt.Value);
		fcd_AddParam("END_DT" , calEndDt.Value);
		fcd_AddParam("RMK"    , edRmk.text);

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

	}
}


/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchGoodType(obj) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	sServiceName = "rem5020-service:searchGoodType";
	sInDataSet   = "";
	sOutDataSet  = "dsGoodType=dsGoodType ";

	//조회조건 : 
	fcd_AddParam("BRNC_CD", cboSearchBrncCd.Value);
	fcd_AddParam("USE_YN",  "Y");

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


