function fcToExcel(obj) {

	var sExcelList = "tab0.tab1.grdGoodSum:지정좌석실 상품 입출고내역";
	if (tab0.TabIndex == 1) {	
		sExcelList = "tab0.tab2.grdIfisList:지정좌석실 지출결의내역";
	}
	fcd_OpenExcelListExport(sExcelList);
}


