Window.Script:
/***************************************************************************************************
*     현재 총 100 컬럼임 되도록 100 컬럼 안으로 코딩을 하세요                                      *
123456789 123456789 123456789 123456789 123456789 123456789 123456789 123456789 123456789 1234567890
***************************************************************************************************/

/************************
*  공통 Script Include  *
************************/
#include "lib::rbm_common_script.js";
#include "lib::rbm_app_script.js";

var sWkJobNm = "";
var sWkJobCd = "";
var sWkJobNmCurrow = "";
var sWkJobCdCurrow = "";
/*-----------------------------------+
|  최초 화면 Load時 처리 할 사항     |
+------------------------------------*/
function fcFormLoadSetting(obj){

	dsWkJob.ClearData();
	fcSearch();
}

/*-----------------------+
|  조회 실행 !  |
+------------------------*/
function fcSearch(obj) {	
	//필수 조회 조건 체크

	var sServiceName = "rev1020-service:searchWkJob";
    var sInDataSet   = "";
    var sOutDataSet  = "dsWkJob=dsWkJob ";

	fcd_AddParam("ESTM_YEAR", sEvalYear); 
	fcd_AddParam("ESTM_NUM" , sEvalNum);
	fcd_AddParam("WK_JOB_CD", edWkJobCd.Text);
	fcd_AddParam("WK_JOB_NM", edWkJobNm.Text);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}

/*------------------------------------+
|  Transaction 후 처리 해야 할 내용!  |
+-------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 완료 후
	if ( sServiceName == "rev1020-service:searchWkJob" ) {

		sSplitCntrItm = split(sCntrItm, "|");

		for(var i=0;i < sSplitCntrItm.length(); i++) {
			var nFindRow = dsWkJob.FindRowNF("WK_JOB_CD", sSplitCntrItm[i]);
			dsWkJob.SetColumn(nFindRow, "COLOR", "1");
		}

		sSplitCntrItm = "";
		sSplitCntrItm = split(sCntrItmCurr, "|");

		for(var i=0; i < sSplitCntrItm.length(); i++) {
			var nFindRow = dsWkJob.FindRowNF("WK_JOB_CD", sSplitCntrItm[i]);
			dsWkJob.SetColumn(nFindRow, "CHK", "1");
		}
		edWkJobNm.SetFocus();
	}
}

/*--------------+
|  확인 처리 !  |
+---------------*/
function fcChoice(obj) {
	for( var i=0;i<dsWkJob.rowcount();i++ ) {
		if (dsWkJob.GetColumn(i,"CHK") == '1') {

			if (fnc_IsNull(sWkJobNmCurrow) == false ) sWkJobNmCurrow = sWkJobNmCurrow +', ' ;
			if (fnc_IsNull(sWkJobCdCurrow) == false ) sWkJobCdCurrow = sWkJobCdCurrow +'|' ;

			sWkJobNm = dsWkJob.GetColumn(i,"WK_JOB_NM");
			sWkJobCd = dsWkJob.GetColumn(i,"WK_JOB_CD");

			sWkJobNmCurrow = sWkJobNmCurrow + sWkJobNm;
			sWkJobCdCurrow = sWkJobCdCurrow + sWkJobCd;
		}

	}

	var arrReturn= array(2);         //결과 반환 할 Array	

	arrReturn[0] = sWkJobNmCurrow;   //사용자 ID 반환
	arrReturn[1] = sWkJobCdCurrow;   //사용자 명 반환

    Close(arrReturn); 
}

/*--------------+
|  취소 처리 !  |
+---------------*/
function fcClose(obj) 
{
	Close(false);     //Null 반환 후 종료
}

/*---------------------------------+
|  Enter Key Press 시 조회 실행 !  |
+----------------------------------*/
function edSearch_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam){

	if (nChar==ENTER_KEY) fcSearch();

}

/*-------------------------------------------------------------------------------------------------+
>>  그리드 정렬
+-------------------------------------------------------------------------------------------------*/
function fcGridSort(obj,nCell,nX,nY) {
	//특정 Head 클릭 시 해당 항목으로 Sort(만약 특정 컬럼을 누를때만 소트하고자 한다면 nCell로 조건)
	fnc_GridSort(obj, object(obj.BindDataset), nCell);
}
