#include "lib::rbm_common_script.js";
#include "lib::rbm_app_script.js";

/*********************
*  화면 변수 선언부  *
*********************/
var sDeptCd = "";
var sDeptNm = "";

/*-----------------------------------+
|  최초 화면 Load時 처리 할 사항     |
+------------------------------------*/
function fcFormLoadSetting(obj){

    dsAppoint.ClearData();

    fcSearch();
}

/*-----------------------+
|  조회 실행 !  |
+------------------------*/
function fcSearch(obj) {	
	//필수 조회 조건 체크

	var sServiceName = "rev1050-service:searchAppoint";
    var sInDataSet   = "";
    var sOutDataSet  = "dsAppoint=dsAppoint ";

	fcd_AddParam("ESTM_YEAR", sEvalYear); 
	fcd_AddParam("ESTM_NUM", sEvalNum);
	fcd_AddParam("EMP_NO", sEmpNo);
	fcd_AddParam("EMP_NM", sEmpNm);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*------------------------------------+
|  Transaction 후 처리 해야 할 내용!  |
+-------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 완료 후
	if ( sServiceName == "rev1050-service:searchAppoint" ) {
		grdList.SetFocus();
	}
}
/*-------------------------------------------------------------------------------------------------+
>>  그리드 정렬
+-------------------------------------------------------------------------------------------------*/
function fcGridSort(obj,nCell,nX,nY) {
	//특정 Head 클릭 시 해당 항목으로 Sort(만약 특정 컬럼을 누를때만 소트하고자 한다면 nCell로 조건)
	fnc_GridSort(obj, object(obj.BindDataset), nCell);
}

/*--------------+
|  확인 처리 !  |
+---------------*/
function fcChoice(obj) {
	//fnc_SetReturnDs(dsWkJob, dsWkJob.GetCurrow(), gdsPopup);

	for( var i=0;i<dsAppoint.rowcount();i++ ) {
		if (dsAppoint.GetColumn(i,"CHK") == '1') {

			sDeptCd = dsAppoint.GetColumn(i,"DEPT_CD");
			sDeptNm = dsAppoint.GetColumn(i,"APT_DT");

		}

	}

	var arrReturn    = array(2);               //결과 반환 할 Array	

	arrReturn[0] = sDeptCd;   //사용자 ID 반환
	arrReturn[1] = sDeptNm;   //사용자 명 반환

    Close(arrReturn);                                                   //반환 후 조회
}

function grdList_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{

	var arrReturn    = array(2);               //결과 반환 할 Array	

	arrReturn[0] = dsAppoint.GetColumn(nRow, "DEPT_CD");   //사용자 ID 반환
	arrReturn[1] = dsAppoint.GetColumn(nRow, "APT_DT");   //사용자 명 반환

    Close(arrReturn);
}

/*--------------+
|  취소 처리 !  |
+---------------*/
function fcClose(obj) {
	Close(false);     //Null 반환 후 종료
}

function dsAppoint_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(dsAppoint.GetColumn(nRow,"CHK") == 1)     //체크박스 선택
		{  

		   for(var i = 0; i<nRow; i++)             //내가 선택한 체크박스 로우에  윗 로우부분과 아래 로우부분은 무조건 0으로 세팅
		   {                                       //예를 들어 내가선택한 로우가  10줄 중에 3번째 줄이면 첫번째줄부터 2번째 줄까지, 4번째 줄에서 10번째 줄까지 0으로 셋팅해줌 
			   dsAppoint.SetColumn(i,"CHK",0); 
		   }
		   for(var i = nRow+1; i<dsAppoint.GetRowCount(); i++)
		   {
			  dsAppoint.SetColumn(i,"CHK",0);
		   }
		}
}
