/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  
    sBUTTONLIST   = 'FT'+sInpt+'FTFT';

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	//공통코드 조회
	fcd_GetComboDs(dsInComCd);

    fcSearch();
}


/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "dsDeptEmp";
	if (!fnc_FormUnloadCheck(sDataset  , sFormId)) return false;
}

/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	//Return Key Press時 Next Component focus
	if ( nChar == 13 ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}

	//Control Key가 눌려져 있다면 단축키 처리
	//if (bControl) fnc_HotKey(this, nChar);
}

/***************************************************************************************************
*                                      공통 버튼 Event 처리 부분                                   *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    dsDeptEmp.ClearData();  
    dsEmpDept.ClearData();  
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
	fcInit(obj);
	return true;
}

function fcSearch(obj)
{
    if ( !fcSearchCheck(obj) ) return;

	fcSearchDeptEmp();
	fcSearchEmpDept();

}
//좌측 트리뷰 조회
function fcSearchDeptEmp(obj)
{
	var sServiceName = "rev1060-service:searchDeptEmp";
    var sInDataSet   = "";
    var sOutDataSet  = "dsDeptEmp=dsDeptEmp ";     

	fcEstmYearValue();	

	fcd_AddParam("ESTM_YEAR", sEvalYear);	
	fcd_AddParam("ESTM_NUM", sEvalNum);	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

//우측 해당 담당자 조회
function fcSearchEmpDept(obj)
{
	var sServiceName = "rev1060-service:searchEmpDept";
    var sInDataSet   = "";
    var sOutDataSet  = "dsEmpDept=dsEmpDept dsConst=dsConst";     

    fcEstmYearValue();	

	fcd_AddParam("ESTM_YEAR", sEvalYear);	
	fcd_AddParam("ESTM_NUM" , sEvalNum);	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj)
{
	var sServiceName  = "rev1060-service:saveEmpDept";
    var sInDataSet    = "dsEmpDept=dsEmpDept:u ";     
    var sOutDataSet   = "";

    fcEstmYearValue();	

	fcd_AddParam("ESTM_YEAR", sEvalYear);	
	fcd_AddParam("ESTM_NUM", sEvalNum);	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {
    var sExcelList = "grdList:부서별담당자";
    fcd_OpenExcelListExport(sExcelList);
}

/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcEnd(obj) {
	//화면 종료
	Close();	
}

/*-------------------------------------------------------------------------------------------------+
>>  그리드 정렬
+-------------------------------------------------------------------------------------------------*/
function fcGridSort(obj,nCell,nX,nY) {
	//특정 Head 클릭 시 해당 항목으로 Sort(만약 특정 컬럼을 누를때만 소트하고자 한다면 nCell로 조건)
	if ( nCell != 0 ) {
		fnc_GridSort(obj, object(obj.BindDataset), nCell);
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 완료 후
	if ( sServiceName == "rev1060-service:searchDeptEmp" ) {
		if (dsDeptEmp.rowcount() == 0) {
			fnc_message("평가대상이 없습니다.\n\n먼저 평가대상저장 화면에서 대상자를 저장해 주세요!");
		}
	}
	// 조회 완료 후
	if ( sServiceName == "rev1060-service:searchEmpDept" ) {
		//화면 수정 막기
		if( dsConst.GetColumn(0, "UPDATE_YN") == "N" ) {
			divBtnLst.btnSave.Enable = false;
		} else {
			divBtnLst.btnSave.Enable = true;
		}
	}
	// 저장완료 후
	if ( sServiceName == "rev1060-service:saveEmpDept" ) {
		//fcSearch();
	}
	// 승인관리 완료 후
	if ( sServiceName == "rev1050-service:saveAprvMana" ) {
		fcSearch();		
	}
}

function fcEstmYearValue(obj)
{
	/* 콤보박스에서 값 가져오기 */
    var nCbValue =cbEstmYear.Value;
	var nCbRow = dsEstmYear.SearchRow("CD=="+nCbValue);

	sEvalYear = dsEstmYear.GetColumn(nCbRow,"ESTM_YEAR");
	sEvalNum = dsEstmYear.GetColumn(nCbRow,"ESTM_NUM");

}


/*  트리뷰 컨트롤   */
function dsDeptEmp_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (strColumnID <> "CHK") return; 

	/* 상위노드 체크 불가 */
	if ( tvDeptEmp.IsRoot(nRow) == true )
	{ 
		dsDeptEmp.FireEvent = false; 
		for (var j=0;j<dsDeptEmp.RowCount();j++)
		{ 
			if ( dsDeptEmp.GetColumn(j, "NUM") == '1' )
			{
				dsDeptEmp.SetColumn(j, "CHK",  "0");
			}
		}

		dsDeptEmp.SetColumn(nRow, "CHK",  "0"); 

		dsDeptEmp.FireEvent = true; 
	}

	/* 하위노드 체크 여부에 따라 우측 데이터 변경  */
	if( dsDeptEmp.GetColumn(nRow, "NUM") == '2') {

		var nEmpNo = dsDeptEmp.GetColumn(nRow,"EMP_NO");
		var nJobTitNm = dsDeptEmp.GetColumn(nRow,"JOB_TIT_NM");
		var nJobTitCd =  dsDeptEmp.GetColumn(nRow,"JOB_TIT_CD");
		var nEmpNm = dsDeptEmp.GetColumn(nRow,"EMP_NM");
		var nDeptCd = dsDeptEmp.GetColumn(nRow,"DEPT_CD");
		var xGrdRow = dsEmpDept.FindRow("DEPT_CD" , nDeptCd);
		var xEmpNo = dsEmpDept.GetColumn(xGrdRow,"EMP_NO");

		if (dsDeptEmp.GetColumn(nRow, "CHK") == '1') //부서별로 1건만 선택할수있도록 제한
		{
			dsDeptEmp.FireEvent = false;
			for (var j=0;j<dsDeptEmp.RowCount();j++)
			{ 
				if(dsDeptEmp.GetColumn(nRow,"DEPT_CD") == dsDeptEmp.GetColumn(j,"DEPT_CD"))
				{
					dsDeptEmp.SetColumn(j, "CHK",  "0");
				} 
			}
			dsDeptEmp.SetColumn(nRow, "CHK", '1');
			dsDeptEmp.FireEvent = true;

			// 체크된 값이 우측 담당자목록에 설정
			dsEmpDept.SetColumn(xGrdRow,"JOB_TIT_NM", nJobTitNm );
			dsEmpDept.SetColumn(xGrdRow,"JOB_TIT_CD", nJobTitCd );
			dsEmpDept.SetColumn(xGrdRow,"EMP_NO", nEmpNo );
			dsEmpDept.SetColumn(xGrdRow,"EMP_NM", nEmpNm );

		} else {
		    // 체크 해제되면 우측 담당자목록에서도 해제
			dsEmpDept.SetColumn(xGrdRow,"JOB_TIT_NM", "" );
			dsEmpDept.SetColumn(xGrdRow,"JOB_TIT_CD", "" );
			dsEmpDept.SetColumn(xGrdRow,"EMP_NO", "" );
			dsEmpDept.SetColumn(xGrdRow,"EMP_NM", "" );
		}
	}
}

//승인이 되었을 경우, 변경하지 못하도록 한다.
function fcDsCanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {
	if( dsConst.rowcount != 0 )
		if(dsConst.GetColumn(0, "UPDATE_YN") == "N") return false;
}
