#include "lib::rbm_common_script.js";
#include "lib::rbm_app_script.js";

/*********************
*  화면 변수 선언부  *
*********************/
var sJobTitNm = "";
var sEmpNo    = "";
var sEmpNm    = "";
var sHpNo     = "";
var sTeamCd   = "";
var sResNo    = "";
var sJobTitCd = "";
var WK_JOB_CD;
var PERM_GBN;

/*-----------------------------------+
|  최초 화면 Load時 처리 할 사항     |
+------------------------------------*/
function fcFormLoadSetting(obj){

	dsDept.ClearData();

    fcSearch();
}

/*-----------------------+
|  조회 실행 !  |
+------------------------*/
function fcSearch(obj)
{	
	//필수 조회 조건 체크
	var sServiceName = "";

	if ( FLAG == 'Y' ) {
		sServiceName = "rev1070-service:searchDept2";
	} else {
		sServiceName = "rev1070-service:searchDept";

		fcd_AddParam("PERM_TEMP_GBN", PERM_GBN); 
		fcd_AddParam("WK_JOB_CD"    , WK_JOB_CD);
	}
    var sInDataSet   = "";
    var sOutDataSet  = "dsDept=dsDept ";

	fcd_AddParam("ESTM_YEAR", sEvalYear); 
	fcd_AddParam("ESTM_NUM", sEvalNum);
	fcd_AddParam("DEPT_CD", nDeptCd);

	fcd_AddParam("EMP_NO", edEmpNo.Text);
	fcd_AddParam("EMP_NM", edEmpNm.Text);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}

/*------------------------------------+
|  Transaction 후 처리 해야 할 내용!  |
+-------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 완료 후
	if ( sServiceName == "rev1070-service:searchDept" ) {
		edEmpNm.SetFocus();
	}
}

/*--------------+
|  확인 처리 !  |
+---------------*/
function fcChoice(obj)
{
	for( var i=0;i<dsDept.rowcount();i++ ) {
		if (dsDept.GetColumn(i,"CHK") == '1') {

			sEmpNo = dsDept.GetColumn(i,"EMP_NO");
			sEmpNm = dsDept.GetColumn(i,"EMP_NM");
			sJobTitNm = dsDept.GetColumn(i,"JOB_TIT_NM");
			sJobTitCd = dsDept.GetColumn(i,"JOB_TIT_CD");
			sResNo = dsDept.GetColumn(i,"RES_NO");
			sHpNo = dsDept.GetColumn(i,"HP_NO");
			sTeamCd = dsDept.GetColumn(i,"TEAM_CD");
		}
	}

	var arrReturn    = array(7);               //결과 반환 할 Array	

	arrReturn[0] = sEmpNo;  
	arrReturn[1] = sEmpNm;     
	arrReturn[2] = sJobTitNm;    
	arrReturn[3] = sJobTitCd;  
	arrReturn[4] = sResNo; 
	arrReturn[5] = sHpNo; 
	arrReturn[6] = sTeamCd; 

    Close(arrReturn);                                                 
}

/*--------------+
|  취소 처리 !  |
+---------------*/
function fcClose(obj) {
	Close(false);     //Null 반환 후 종료
}

/*---------------------------------+
|  Enter Key Press 시 조회 실행 !  |
+----------------------------------*/
function edSearch_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam){

	if (nChar==13) fcSearch();

}

/*-------------------------------------------------------------------------------------------------+
>>  그리드 정렬
+-------------------------------------------------------------------------------------------------*/
function fcGridSort(obj,nCell,nX,nY) {
	//특정 Head 클릭 시 해당 항목으로 Sort(만약 특정 컬럼을 누를때만 소트하고자 한다면 nCell로 조건)
	fnc_GridSort(obj, object(obj.BindDataset), nCell);
}

function fcDsOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	switch (obj.id )
	{
		case "dsDept" :
			if(dsDept.GetColumn(nRow,"CHK") == 1)     //체크박스 선택
			{  

			   for(var i = 0; i<nRow; i++)             //내가 선택한 체크박스 로우에  윗 로우부분과 아래 로우부분은 무조건 0으로 세팅
			   {                                       //예를 들어 내가선택한 로우가  10줄 중에 3번째 줄이면 첫번째줄부터 2번째 줄까지, 4번째 줄에서 10번째 줄까지 0으로 셋팅해줌 
				   dsDept.SetColumn(i,"CHK",0); 
			   }
			   for(var i = nRow+1; i<dsDept.GetRowCount(); i++)
			   {
				  dsDept.SetColumn(i,"CHK",0);
			   }
			}
		break;		
	}
}

function fcGrdOnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	switch (obj.id ) 
	{
		case "grdList" :
			var arrReturn    = array(7);               //결과 반환 할 Array	

			arrReturn[0] = dsDept.GetColumn(nRow, "EMP_NO");   
			arrReturn[1] = dsDept.GetColumn(nRow, "EMP_NM");   
			arrReturn[2] = dsDept.GetColumn(nRow, "JOB_TIT_NM");
			arrReturn[3] = dsDept.GetColumn(nRow, "JOB_TIT_CD");
			arrReturn[4] = dsDept.GetColumn(nRow, "RES_NO");
			arrReturn[5] = dsDept.GetColumn(nRow, "HP_NO");
			arrReturn[6] = dsDept.GetColumn(nRow, "TEAM_CD");

			Close(arrReturn);
		break;
	}
}
