/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  
    sBUTTONLIST   = 'FTTFTFT';

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

    //공통코드 조회
	fcd_GetComboDs(dsInComCd);

}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "dsEmp";
	if (!fnc_FormUnloadCheck(sDataset  , sFormId)) return false;
}

/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	//Return Key Press時 Next Component focus
	if ( nChar == ENTER_KEY ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}

	//Control Key가 눌려져 있다면 단축키 처리
	//if (bControl) fnc_HotKey(this, nChar);
}

/***************************************************************************************************
*                                      공통 버튼 Event 처리 부분                                   *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    dsEmp.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
	fcInit(obj);
	return true;
}

function fcSearch(obj)
{
    if ( !fcSearchCheck(obj) ) return;

	var	sServiceName = "rev1110-service:searchEstmDept";
	var	sInDataSet   = "";
	var	sOutDataSet  = "dsEstmDept=dsEstmDept dsEmp=dsEmp";     

	fcEstmYearValue();	

	fcd_AddParam("ESTM_YEAR"  , sEvalYear);	
	fcd_AddParam("ESTM_NUM"   , sEvalNum);	
	fcd_AddParam("APRV_DEPT"  , "");
	fcd_AddParam("APRV_SEQ"   , "13");
	fcd_AddParam("DEPT_CD"    , cboDept.Value);
	fcd_AddParam("CNTR_ITM_CD", choCntrItm.Value);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}



/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSaveCheck(obj)
{ 	
	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsEmp)) {
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	for ( var i = 0; i<dsEmp.RowCount(); i++) {
		if( dsEmp.GetColumn(i, "MULT_ESTM_GRP") == null ) {
			fnc_Message(SNIS_RBM_F005, "N");
			return;
		}
	}
	return true;
}

function fcSave(obj)
{
	var sServiceName = "rev1110-service:saveEstmDept";
	var sInDataSet   = "dsEstmDept=dsEstmDept:u ";
	var sOutDataSet  = "";		

	fcEstmYearValue();

	fcd_AddParam("ESTM_YEAR", sEvalYear);	
	fcd_AddParam("ESTM_NUM" , sEvalNum);	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

function fcSaveSrvKCSI() {
	var sServiceName = "rev1110-service:saveSrvKCSI";
	var sInDataSet   = "";
	var sOutDataSet  = "";		

	fcEstmYearValue();

	fcd_AddParam("ESTM_YEAR", sEvalYear);	
	fcd_AddParam("ESTM_NUM" , sEvalNum);	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

function fcApprSave(){

	var sServiceName = "rev1110-service:saveMultAppr";
	var sInDataSet   = "";
	var sOutDataSet  = "";		

	fcEstmYearValue();	

	fcd_AddParam("ESTM_YEAR" , sEvalYear);	
	fcd_AddParam("ESTM_NUM"  , sEvalNum);		
	fcd_AddParam("DEPT_CD"   , sDeptCd);
	fcd_AddParam("GRANT"     , "N");	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);				

}


/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {
    var sExcelList = "grdEstmDept:평가부서 관리";
    fcd_OpenExcelListExport(sExcelList);
}


/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcEnd(obj) {
	//화면 종료
	Close();	
}


