/*-------------------------------------------------------------------------------------------------+
>>  그리드 정렬
+-------------------------------------------------------------------------------------------------*/
function fcGridSort(obj,nCell,nX,nY) {
	//특정 Head 클릭 시 해당 항목으로 Sort(만약 특정 컬럼을 누를때만 소트하고자 한다면 nCell로 조건)
	if ( nCell != 0 ) {
		fnc_GridSort(obj, object(obj.BindDataset), nCell);
	}
}


/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회 완료후 
	if (sServiceName == "common-service:searchCode") {
		fcSearch();
	}
	// 조회 완료 후
	if ( sServiceName == "rev1110-service:searchEstmDept") {

		if(dsEmp.rowcount == 0) return;
		fcSetFilterEmp(dsEstmDept.row);

		//승인시, '그룹생성', '평가대상생성' 비활성화
		if( dsAprvStas.GetColumn(0, "APRV_STAS") == "002" ||
			dsAprvStas.GetColumn(0, "APRV_STAS") == "003") {
			tab0.tab1.groupBtn.Enable = false;
			tab0.tab1.ApprBtn .Enable = false;
		} else {
			//담당자만 버튼 '평가대상선정', '그룹생성' 권한
			if( dsMng.GetColumn(0, "MNG_NO") == gdsUser.GetColumn(0, "USER_ID") ) {
				tab0.tab1.groupBtn.Enable = true;
				tab0.tab1.ApprBtn .Enable = true;

				//'평가대상선정'이 미완료되었다면 '승인요청' 불가능
				if( dsCnt.GetColumn(0, "CNT") > 0 ) {
					aprvReqBtn.Enable = true;
				} else {
					aprvReqBtn.Enable = false;
				}
			} else {
				tab0.tab1.groupBtn.Enable = false;
				tab0.tab1.ApprBtn .Enable = false;
			}
		}

		//승인권한에 따른 버튼처리
		var sAprvEmpNo = dsAprvStas.GetColumn(dsAprvStas.currow, "APRV_OFIR_NO");	//승인자
		var sReqEmpNo  = dsAprvStas.GetColumn(dsAprvStas.currow, "APRV_REQ_NO");	//요청자
		var sAprvCd    = dsAprvStas.GetColumn(dsAprvStas.currow, "APRV_STAS");	    //상태값
		var sLoginUser = gdsUser.GetColumn(gdsUser.currow, "USER_ID");

		var arrBtn = array(5);
		arrBtn[0] = aprvCanCelBtn;
		arrBtn[1] = aprvOkBtn;
		arrBtn[2] = aprvBackBtn;
		arrBtn[3] = aprvReqBtn;
		arrBtn[4] = aprvCallBtn;

		fnc_BtnVisibleInit(arrBtn);
		fnc_AprvBtnLoc(sAprvEmpNo, sReqEmpNo, sLoginUser, sAprvCd, stAprBtnPos.Top, stAprBtnPos.right-aprvCallBtn.width-1);

		/* 탭안에 있으므로 버튼 위치 조정 불필요
		if( aprvReqBtn   .Visible || 
			aprvCallBtn  .Visible ||
			aprvCanCelBtn.Visible ) {
			tab0.tab1.ApprBtn.Left = tab0.tab1.grdList.Right - tab0.tab1.ApprBtn.Width - aprvReqBtn.Width - 4;
		} else if( aprvOkBtn.Visible ) {
			tab0.tab1.ApprBtn.Left =  tab0.tab1.grdList.Right - tab0.tab1.ApprBtn.Width - aprvReqBtn.Width * 2 - 6;
		} else if( !aprvReqBtn.Visible && !aprvCallBtn.Visible && !aprvCanCelBtn.Visible &&
		           !aprvOkBtn.Visible  && !aprvBackBtn.Visible ) {
			tab0.tab1.ApprBtn.Left = tab0.tab1.grdList.Right-tab0.tab1.ApprBtn.Width;
		}
		tab0.tab1.groupBtn.Left = tab0.tab1.ApprBtn.Left - tab0.tab1.groupBtn.Width;
		*/

		//평가 개시가 되었을 경우 
		if( dsConst.GetColumn(0, "ESTM_OPN_YN") == "Y" ) {
			divBtnLst.btnSave.Enable = false;

			aprvCanCelBtn.Enable = false;
			aprvOkBtn    .Enable = false;
			aprvBackBtn  .Enable = false;
			aprvReqBtn   .Enable = false;
			aprvCallBtn  .Enable = false;
			tab0.tab1.groupBtn     .Enable = false;
			tab0.tab1.ApprBtn      .Enable = false;

			tab0.tab1.grdList.Editable = false;
		} else {
			divBtnLst.btnSave.Enable = true;
			aprvCanCelBtn.Enable = true;
		}

		//그룹생성
		if( sGroupClick == "Y" ) {
			sGroupClick = "N";
			fcSetGrp();
		}
	}			
	if ( sServiceName == "rev1110-service:searchEmpTotGrp") {

		//승인시, '그룹생성', '평가대상생성' 비활성화
		if( dsAprvStas.GetColumn(0, "APRV_STAS") == "002" ||
			dsAprvStas.GetColumn(0, "APRV_STAS") == "003") {
			tab0.tab2.BtnSaveTotGrp.Enable = false;
		} else {
			//담당자만 버튼 '평가대상선정', '그룹생성' 권한
			if( dsMng.GetColumn(0, "MNG_NO") == gdsUser.GetColumn(0, "USER_ID") ) {
				tab0.tab2.BtnSaveTotGrp.Enable = true;
			} else {
				tab0.tab2.BtnSaveTotGrp.Enable = false;
			}
		}

		//평가 개시가 되었을 경우 
		if( dsConst.GetColumn(0, "ESTM_OPN_YN") == "Y" ) {
			tab0.tab2.BtnSaveTotGrp.Enable = false;
			tab0.tab2.grdDeptTotGrp.Editable = false;
		} else {
			tab0.tab2.BtnSaveTotGrp.Enable = true;
			tab0.tab2.grdDeptTotGrp.Editable = true;
		}

	}				


	if ( sServiceName == "rev1110-service:searchCbCntr" )
	{
		if (dsEmp.RowCount() == 0) fcSearch();	
	}	
	// 저장 완료 후
	if ( sServiceName == "rev1110-service:saveEmp" ) {
		var ARRAY2_GBL_RET_VALUE = split2(GBL_RET_VALUE,"^","=");
        var sResult = fnc_Array2Value(ARRAY2_GBL_RET_VALUE, "RESULT"); // 결과

        if( sResult == "S" && tab0.TabIndex == 0) {	//정상적으로 저장이 되었을 떄
            fcApprSave();
        }
	}	

	// 승인관리 완료 후
	if ( sServiceName == "rev1050-service:saveAprvMana" ) {
		fcSearch();		
	}

	// 평가대상선정 완료 후
	if ( sServiceName == "rev1110-service:saveMultAppr" ) {
		fcSearch();		
	}
}

function fcEstmYearValue(obj)
{
	/* 콤보박스에서 값 가져오기 */
    var nCbValue =cbEstmYear.Value;
	var nCbRow = dsEstmYear.SearchRow("CD=="+nCbValue);

	sEvalYear = dsEstmYear.GetColumn(nCbRow,"ESTM_YEAR");
	sEvalNum  = dsEstmYear.GetColumn(nCbRow,"ESTM_NUM");
	sDeptCd   = cboDept.Value;
}

function fcDsOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	switch(obj.id) {
		case "dsEmp" :
			if ( strColumnID = "MULT_ESTM_GRP" ) {
				var nMultEvGrp = dsEmp.GetColumn(nRow, "MULT_ESTM_GRP");

				if ( !fnc_IsNull(varNewValue) && toInteger(varNewValue) <= 0 ) {
					fnc_Message(SNIS_RBM_F003, "N");
					obj.SetColumn(nRow, strColumnID , "");
					tab0.tab1.grdList.SetFocus();
					return;
				}

				//한 그룹이 10명이 초과되는 것을 막아줌.
				var nCnt = 0;
				var sDeptCd = obj.GetColumn(nRow, "DEPT_CD");
				for(var i=0; i<obj.rowcount; i++) {
					if( obj.GetColumn(i, "MULT_ESTM_GRP") == varNewValue &&
						obj.GetColumn(i, "DEPT_CD")     == sDeptCd ) {
						nCnt++;
					}
				}

				if( !fnc_IsNull(varNewValue) && nCnt > 10 ) {
					fnc_Message(SNIS_RBM_F004, "N");
					obj.SetColumn(nRow, strColumnID, "");
					tab0.tab1.grdList.SetFocus();
					return;
				}
			}
	}
}


//평가대상생성 Check
function fcBtnCheck() {
	//다면평가그룹 데이터 없을 시
	if( dsEmp.rowcount == 0 ) {
		fnc_Message(SNIS_COM_1053, "N");
		return false;
	}

	//다면평가그룹 필수항목
	for(var i = 0; i < dsEmp.rowcount; i++) {
		if( fnc_IsNull( dsEmp.GetColumn(i, "MULT_ESTM_GRP")) ) {
			fnc_Message(SNIS_RBM_D001, "N", i+1, "다면평가그룹");
			dsEmp.row = i;
			return false;
		}
	}

	return true;
}

//버튼 Click
function fcBtnOnClick(obj) {
	switch( obj.id ) {
		case "ApprBtn" :
			if ( !fcValid() ) return;
			if ( !fcBtnCheck() ) return;

			if (fnc_Message(SNIS_RBM_F034, "Y")) fcDataSave(); 	// 다면평가등급 저장

			break;
		case "aprvReqBtn" :	//승인요청
			if ( !fcBtnCheck() ) return;

			if(fnc_Message(SNIS_RBM_F032, "Y", "승인요청")) {  
				var sServiceName = "rev1050-service:saveAprvMana";
				var sInDataSet   = "";
				var sOutDataSet  = "";    	

				fcEstmYearValue();	

				fcd_AddParam("ESTM_YEAR", sEvalYear);	
				fcd_AddParam("ESTM_NUM" , sEvalNum);
				fcd_AddParam("MENU_ID"  , "REV1110");
				fcd_AddParam("APRVCD"   , "01");		//승인요청	

				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
			}
			break;

		case "aprvCallBtn" :	//회수
			if(fnc_Message(SNIS_RBM_F032, "Y", "회수")) {  
				var sServiceName = "rev1050-service:saveAprvMana";
				var sInDataSet   = "";
				var sOutDataSet  = "";    	

				fcEstmYearValue();	

				fcd_AddParam("ESTM_YEAR", sEvalYear);	
				fcd_AddParam("ESTM_NUM" , sEvalNum);
				fcd_AddParam("MENU_ID"  , "REV1110");
				fcd_AddParam("APRVCD"   , "02");		//승인요청	

				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
			}
			break;
		case "aprvOkBtn" :	//승인
			if(fnc_Message(SNIS_RBM_F032, "Y", "승인")) {  
				var sServiceName = "rev1050-service:saveAprvMana";
				var sInDataSet   = "";
				var sOutDataSet  = "";    	

				fcEstmYearValue();	
				var sCurDate = fcd_GetSysdate("YYYYMMDD");

				fcd_AddParam("ESTM_YEAR", sEvalYear);	
				fcd_AddParam("ESTM_NUM" , sEvalNum);
				fcd_AddParam("MENU_ID"  , "REV1110");
				fcd_AddParam("APRVCD"   , "03");			//승인요청	
				fcd_AddParam("APRVDATE" , sCurDate);		//승인날짜

				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
			}
			break;
		case "aprvBackBtn" :	//반려
			if(fnc_Message(SNIS_RBM_F032, "Y", "반려")) {  
				var sServiceName = "rev1050-service:saveAprvMana";
				var sInDataSet   = "";
				var sOutDataSet  = "";    	

				fcEstmYearValue();	

				fcd_AddParam("ESTM_YEAR", sEvalYear);	
				fcd_AddParam("ESTM_NUM" , sEvalNum);
				fcd_AddParam("MENU_ID"  , "REV1110");
				fcd_AddParam("APRVCD"   , "04");							//승인요청	

				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
			}
			break;
		case "aprvCanCelBtn" :	//승인취소
			if(fnc_Message(SNIS_RBM_F032, "Y", "승인취소")) {  
				var sServiceName = "rev1050-service:saveAprvMana";
				var sInDataSet   = "";
				var sOutDataSet  = "";    	

				fcEstmYearValue();	

				fcd_AddParam("ESTM_YEAR", sEvalYear);	
				fcd_AddParam("ESTM_NUM" , sEvalNum);
				fcd_AddParam("MENU_ID"  , "REV1110");
				fcd_AddParam("APRVCD"   , "05");							//승인요청	

				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
			}
			break;
		case "BtnSaveSrvKCSI":
			if ( !fcValid() ) return;
			if (fnc_Message("KCSI평가점수를 개인별 서비스평가에 반영하시겠습니까?", "Y")) fcSaveSrvKCSI();	// 종합평가등급 (부서별) 저장
			break;
	}
}


function fcChoOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	switch( obj.id ) {
		case "cboDept" :
			fcSearch();
			break;
		case "choCntrItm" :
			fcSearch();
			break;
	}
}

function fcDsOnRowPosChanged(obj,nOldRow,nRow)
{
	if (obj.id == "dsEstmDept") {
		fcSetFilterEmp(nRow);
	}
}
function fcSetFilterEmp(nRow)
{
	var sDeptCd = dsEstmDept.GetColumn(nRow, "DEPT_CD");
	dsEmp.Filter("DEPT_CD='" + sDeptCd + "'");
}
