/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  
    sBUTTONLIST   = 'FTFFFFT';

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

    //공통코드 조회
	fcd_GetComboDs(dsInComCd);

	stEval.Text = dsYearNum.GetColumn(0, "ESTM_YEAR") + "년 "             
				+ dsYearNum.GetColumn(0, "ESTM_NUM")  + "회차 평가("
				+ substr(dsYearNum.GetColumn(0, "PRM_STR_DT"), 0, 4) + "."
				+ substr(dsYearNum.GetColumn(0, "PRM_STR_DT"), 4, 2) + "." 
				+ substr(dsYearNum.GetColumn(0, "PRM_STR_DT"), 6, 2) + "~" 
				+ substr(dsYearNum.GetColumn(0, "PRM_END_DT"), 0, 4) + "." 
				+ substr(dsYearNum.GetColumn(0, "PRM_END_DT"), 4, 2) + "." 
				+ substr(dsYearNum.GetColumn(0, "PRM_END_DT"), 6, 2) + ")";

	if( dsYearNum.GetColumn(0, "ESTM_OPN_YN") == "Y" ) {
		fcOpenComplete();

		if( dsYearNum.GetColumn(0, "ESTM_END_YN") == "Y" ) {
			fcCloseComplete();
		}
	} else {
		if( dsYearNum.GetColumn(0, "ESTM_END_YN") == "Y" ) {
			fcCloseComplete();
		}
	}

	fcSearch();
}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는 DataSet Object를 넘긴다.
	var sDataset = "";
	if ( !fnc_FormUnloadCheck(sDataset, sFormId) ) return false;
}


/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcEnd(obj) {
	//화면 종료
	Close();	
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj)
{
	dsEmpDept.ClearData();
	dsEmpEstm.ClearData();

	var sServiceName = "rev3020-service:searchData";
    var sInDataSet   = "";
    var sOutDataSet  = "dsPrm=dsPrm "
					 + "dsMst=dsMst "
					 + "dsPrm=dsPrm "
					 + "dsDistribution=dsDistribution "
					 + "dsEmp=dsEmp "
					 + "dsEmpDept=dsEmpDept "
					 + "dsEmpEstm=dsEmpEstm "
					 + "dsEmpMult=dsEmpMult "
					 + "dsPrmItm1=dsPrmItm1 "
					 + "dsPrmItm2=dsPrmItm2 "
					 + "dsMnrItm=dsMnrItm "
					 + "dsSvrItm=dsSvrItm "
					 + "dsMultItm=dsMultItm "
					 + "dsEstmSte=dsEstmSte ";

    fcd_AddParam("ESTM_YEAR", dsYearNum.GetColumn(0, "ESTM_YEAR"));	
	fcd_AddParam("ESTM_NUM" , dsYearNum.GetColumn(0, "ESTM_NUM"));	

  	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

function saveEstmEnd(obj)
{
	dsEstmEnd.SetColumn(0,"ESTM_YEAR", dsYearNum.GetColumn(0, "ESTM_YEAR") );
	dsEstmEnd.SetColumn(0,"ESTM_NUM", dsYearNum.GetColumn(0, "ESTM_NUM") );

	var sServiceName = "rev3020-service:saveEstmEnd";
	var sInDataSet   = "dsEstmEnd=dsEstmEnd:u "
					 + "dsExcept=dsExcept:a "
					 + "dsNoEval=dsNoEval:a ";
	var sOutDataSet  = "";

	fcd_AddParam("ESTM_YEAR", dsYearNum.GetColumn(0, "ESTM_YEAR"));	
	fcd_AddParam("ESTM_NUM" , dsYearNum.GetColumn(0, "ESTM_NUM"));	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}

function searchDeptEmp(obj)
{
	var sServiceName = "rev3020-service:searchDeptEmp";
    var sInDataSet   = "";
    var sOutDataSet  = "dsDeptEmp=dsDeptEmp ";     

	fcd_AddParam("ESTM_YEAR", dsYearNum.GetColumn(0, "ESTM_YEAR"));	
	fcd_AddParam("ESTM_NUM" , dsYearNum.GetColumn(0, "ESTM_NUM"));	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}
/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 완료 후
	if ( sServiceName == "rev3020-service:searchData" )
	{
		if( dsEmpDept.rowcount == 0 ) {
			var nRow = dsEmpDept.AddRow();
			dsEmpDept.SetColumn(nRow, "CNT_4", "미완료");
		}

		if( dsEmpEstm.rowcount == 0 ) {
			var nRow = dsEmpEstm.AddRow();
			dsEmpEstm.SetColumn(nRow, "CNT_6", "미완료");
		}


		if(dsEstmSte.GetRowCount() >0){
			if( dsEstmSte.GetColumn(0, "ESTM_OPN_YN") == "Y" ) {
				fcOpenComplete();

				if( dsEstmSte.GetColumn(0, "ESTM_END_YN") == "Y" ) {
					fcCloseComplete();
				}
			} else {
				fcOpenStart();

				if( dsEstmSte.GetColumn(0, "ESTM_END_YN") == "Y" ) {
					fcCloseComplete();
				}
			}
	    }
	}			

	//평가마감전
	if ( sServiceName == "rev3020-service:searchExcept" ) {
		saveEstmEnd();
	}

	//평가마감 종료 후
	if ( sServiceName == "rev3020-service:saveEstmEnd" ) {
		fcCloseComplete();
		dsYearNum.SetColumn(0, "ESTM_END_YN", "Y");
	}

	//평가개시 버튼 
	if ( sServiceName == "rev3020-service:searchStart" ) {

		var sMsg = "";

		if( dsComplete.GetColumn(0, "COMPLETE_YN") == "Y" ) {
			sMsg = SNIS_RBM_F011;
		} else {
			sMsg = SNIS_RBM_F012;
		}

		if( fnc_Message(sMsg, "Y") ) {

			var sServiceName = "rev3020-service:saveStart";
			var sInDataSet   = "";
			var sOutDataSet  = ""; 	

			//서버로 넘길 값
			fcd_AddParam("ESTM_YEAR", dsYearNum.GetColumn(0, "ESTM_YEAR"));
			fcd_AddParam("ESTM_NUM" , dsYearNum.GetColumn(0, "ESTM_NUM"));

			fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
		}
	}

	// 개시 이후 
	if( sServiceName == "rev3020-service:saveStart" ) {
		dsYearNum.SetColumn(0, "ESTM_OPN_YN", "Y");
		fcOpenComplete();
	}
}


function fcBtnOnClick(obj)
{
	switch ( obj.id )
	{
		case "btnMst" :
			global.FrameBottom.fcOpenMdiForm("REV1010", "", "frm_rev::REV1010.xml");
		break;

		case "btnPrm" :
			global.FrameBottom.fcOpenMdiForm("REV1020", "", "frm_rev::REV1020.xml");
		break;

		case "btnDistribution" :
			global.FrameBottom.fcOpenMdiForm("REV1040", "", "frm_rev::REV1040.xml");
		break;

		case "btnEmp" :
			global.FrameBottom.fcOpenMdiForm("REV1050", "", "frm_rev::REV1050.xml");
		break;

		case "btnEmpDept" :
			global.FrameBottom.fcOpenMdiForm("REV1060", "", "frm_rev::REV1060.xml");
		break;

		case "btnEmpEstm" :
			global.FrameBottom.fcOpenMdiForm("REV3030", "", "frm_rev::REV1070.xml");
		break;

		case "btnEmpMult" :
			global.FrameBottom.fcOpenMdiForm("REV1080", "", "frm_rev::REV1080.xml");
		break;

		case "btnPrmItm" :
			global.FrameBottom.fcOpenMdiForm("REV3010", "", "frm_rev::REV3010.xml");
		break;

		case "btnMnrItm" :
			global.FrameBottom.fcOpenMdiForm("REV3010", "", "frm_rev::REV3010.xml");
		break;

		case "btnSvrItm" :
			global.FrameBottom.fcOpenMdiForm("REV3010", "", "frm_rev::REV3010.xml");
		break;

		case "btnMultItm" :
			global.FrameBottom.fcOpenMdiForm("REV3010", "", "frm_rev::REV3010.xml");
		break;

		case "btnEstmEnd" :
			if( fnc_Message(SNIS_RBM_F037, "Y") ) {
				var sServiceName = "rev3020-service:searchExcept";
				var sInDataSet   = "";
				var sOutDataSet  = "dsExcept=dsExcept dsNoEval=dsNoEval "; 	

				 //조회조건
				fcd_AddParam("ESTM_YEAR", dsYearNum.GetColumn(0, "ESTM_YEAR"));
				fcd_AddParam("ESTM_NUM" , dsYearNum.GetColumn(0, "ESTM_NUM"));

				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
			}
		break;

		case "btnEstmStart" : //평가개시

			var sServiceName = "rev3020-service:searchStart";
			var sInDataSet   = "";
			var sOutDataSet  = "dsComplete=dsComplete "; 	

			//서버로 넘길 값
			fcd_AddParam("ESTM_YEAR", dsYearNum.GetColumn(0, "ESTM_YEAR"));
			fcd_AddParam("ESTM_NUM" , dsYearNum.GetColumn(0, "ESTM_NUM"));

			fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
		break;
	}
}

function fcOpenComplete() {

	btnEstmStart.Enable = false;
	btnEstmStart.Text   = "개시완료";
}

function fcOpenStart() {

	btnEstmStart.Enable = true;
	btnEstmStart.Text   = "평가개시";
}



function fcCloseComplete() {
	btnEstmEnd.Enable = false;
	btnEstmEnd.Text   = "마감완료";
}
