/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  
    sBUTTONLIST   = 'FTFFFFT';

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

    //공통코드 조회
	fcd_GetComboDs(dsInComCd);
    fcSearchComboDept();

}
/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "";
	if (!fnc_FormUnloadCheck(sDataset  , sFormId)) return false;
}

/*-------------------------------------------------------------------------------------------------+
>>  초기화
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj)
{
    dsPrmItm1.ClearData(); 
	dsPrmItm2.ClearData(); 
	dsMnrItm.ClearData(); 
	dsSvrItm.ClearData(); 
	dsMultItm.ClearData(); 
	dsPrm.ClearData(); 
	dsMnr.ClearData(); 
	dsSvr.ClearData(); 
	dsMult.ClearData(); 
}

/*-------------------------------------------------------------------------------------------------+
>>  조회
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    fcInit(obj);
	return true;
}

function fcSearch(obj)
{
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rev3040-service:searchData";
    var sInDataSet   = "";
    var sOutDataSet  = "dsPrmItm1=dsPrmItm1 "
				 	 + "dsPrmItm2=dsPrmItm2 "
					 + "dsMnrItm=dsMnrItm "
					 + "dsSvrItm=dsSvrItm "
					 + "dsMultItm=dsMultItm "
					 + "dsPrm=dsPrm "
					 + "dsMnr=dsMnr "
					 + "dsSvr=dsSvr "
					 + "dsMult=dsMult ";

	fcd_AddParam("ESTM_YEAR", sEvalYear);	
	fcd_AddParam("ESTM_NUM" , sEvalNum);	
	fcd_AddParam("ESTM_DEPT", cboDept.Value);	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

function fcSearchComboDept(obj)
{
	dsDept.ClearData();

	var sServiceName = "rev1070-service:searchCbDept";
    var sInDataSet   = "";
    var sOutDataSet  = "dsDept=dsDept ";     

	fcEstmYearValue();	

	fcd_AddParam("ESTM_YEAR", sEvalYear);	
	fcd_AddParam("ESTM_NUM", sEvalNum);	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

function fcCheckAppr(obj)
{
	var sServiceName = "rev1070-service:searchCheckAppr";
    var sInDataSet   = "";
    var sOutDataSet  = "dsAppr=dsAppr ";     

	fcEstmYearValue();	

	fcd_AddParam("ESTM_YEAR", sEvalYear);	
	fcd_AddParam("ESTM_NUM", sEvalNum);	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}
/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcEnd(obj) {
	//화면 종료
	Close();	
}


/***************************************************************************************************
*                                      공통 Function 처리 부분                                     *
***************************************************************************************************/

/*-------------------------------------------------------------------------------------------------+
>>  그리드 정렬
+-------------------------------------------------------------------------------------------------*/
function fcGridSort(obj,nCell,nX,nY) {
	//특정 Head 클릭 시 해당 항목으로 Sort(만약 특정 컬럼을 누를때만 소트하고자 한다면 nCell로 조건)
	if ( nCell == 0 ) {
		//다면평가 탭에서만 작동함
		if (obj.GetCellProp("head",0,"text") == 1){
			obj.SetCellProp("head",0,"text", "0");
			for ( var i = 0; i< dsMult.RowCount(); i++) {
				dsMult.SetColumn(i, "CHK" , 0);
			}
		} else {
			obj.SetCellProp("head",0,"text", "1");
			for ( var i = 0; i< dsMult.RowCount(); i++) {
				dsMult.SetColumn(i, "CHK" , 1);
			}
		}

	} else {
		fnc_GridSort(obj, object(obj.BindDataset), nCell);
	}
}
/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 완료 후
	if ( sServiceName == "rev3040-service:searchData" ){

	}	

	//  부서 콤보 조회 완료 후
	if ( sServiceName == "rev1070-service:searchCbDept" ) {
		fcCheckAppr();
	}

	if ( sServiceName == "rev1070-service:searchCheckAppr" ) {		
		if ( dsAppr.GetColumn(0, "EMP_NO") == gdsUser.GetColumn(0, "USER_ID") || 
		     dsAppr.GetColumn(1, "EMP_NO") == gdsUser.GetColumn(0, "USER_ID")) {

			cboDept.Enable = true;
			btnSendMsg.Enable = true;			
			btnSendSms.Enable = true;
		} else {
			var sDeptCd = substr(gdsUser.GetColumn(0, "TEAM_DETL_CD"), length(gdsUser.GetColumn(0, "TEAM_DETL_CD"))-4, 4);
			idle();
			cboDept.Index  = dsDept.FindRowNF("DEPT_CD", sDeptCd);
			cboDept.Enable = false;
			btnSendMsg.Enable = false;			
			btnSendSms.Enable = false;
		}
		fcSearch();	
	}
}

function fcEstmYearValue(obj)
{
	/* 콤보박스에서 값 가져오기 */
    var nCbValue =cbEstmYear.Value;
	var nCbRow = dsEstmYear.SearchRow("CD=="+nCbValue);

	sEvalYear = dsEstmYear.GetColumn(nCbRow,"ESTM_YEAR");
	sEvalNum = dsEstmYear.GetColumn(nCbRow,"ESTM_NUM");

}


function fcCheckCombo(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcSearchComboDept();
}

function fcBtnOnClick(obj)
{
	var nDataSet = "" ;
	if (tab.TabIndex == 0){
		nDataSet = dsPrm;
	} else if (tab.TabIndex == 1){
		nDataSet = dsMnr;
	} else if (tab.TabIndex == 2){
		nDataSet = dsSvr;
	} else if (tab.TabIndex == 3){
		nDataSet = dsMult;
	}

	switch ( obj.id )
	{		

		case "btnSendSms" :	//SMS 전송	문자		

			var sSendPhoneNumber 	= gdsUser.GetColumn(0,"HP_NO");	 //송신자이동전화번호 
			var sSendUserId 	 	= gdsUser.GetColumn(0,"USER_ID");//송신자 사번
			var sSendUserName 		= gdsUser.GetColumn(0,"USER_NM");//송신자명 
			var sReceivePhoneNumber = "";							 //수신자이동전화번호 , 여러명인경우 ; 로 구분 
			var sReceiveName 		= "";							 //수신자이동전화번호 , 여러명인경우 ; 로 구분 
			var sReceiveId          = "";
			var sParams				= "";
			var sTitle              = "종사원평가(부서별 진행현황)";
			var sPermTempGbn        = "001";							// 수신자가 정규직인지 여부

			//fnc_IsNull( nDataSet.GetColumn (0,"MNG_NO") ) return;
			//fnc_IsNull( nDataSet.GetColumn (0,"MNG_NO") ) return;

			if(tab.TabIndex == 3) {
				for(var i = 0; i <nDataSet.GetRowCount();i++){
					if(nDataSet.GetColumn(i,"CHK") == "1"){
						sReceivePhoneNumber += nDataSet.GetColumn(i,"HP_NO")+";";					
						sReceiveName        += nDataSet.GetColumn(i,"EMP_NM")+";";
						sReceiveId          += nDataSet.GetColumn(i,"EMP_NO")+";";					
					}							
				}	
				sPermTempGbn = "002";
			} else {
				sReceivePhoneNumber =  nDataSet.GetColumn(0,"HP_NO")+";";
				sReceiveName =  nDataSet.GetColumn(0,"MNG_NM")+";";
				sReceiveId =  nDataSet.GetColumn(0,"MNG_NO")+";";
			}
			sReceivePhoneNumber = substr(sReceivePhoneNumber,0,LENGTH(sReceivePhoneNumber)-1);
			sReceiveName = substr(sReceiveName,0,LENGTH(sReceiveName)-1);
			sReceiveId = substr(sReceiveId,0,LENGTH(sReceiveId)-1);

			sParams = "sSendPhoneNumber="+quote(sSendPhoneNumber);
			sParams += " sSendUserId="+quote(sSendUserId);
			sParams += " sSendUserName="+quote(sSendUserName);
			sParams += " sReceivePhoneNumber="+quote(sReceivePhoneNumber);
			sParams += " sReceiveName="+quote(sReceiveName);
			sParams += " sReceiveId="+quote(sReceiveId);
			sParams += " sTitle="+quote(sTitle);
			sParams += " sRecvrUserType="+quote(sPermTempGbn);

			nHandle = Open("common::comSendSms.xml", sParams, 240, 328, "Title=false");

		break;

		case "btnSendMsg" :	//쪽지 전송			

			var sSendUser  	= gdsUser.GetColumn(0,"USER_ID");	 //송신자사번 
			var sRecvUser   = "";//수신자사번
			var sTitle   	= "평가부서준비현황";//제목 
			var sLinkUrl 	= "";							 //
			var sDesc   	= "";							 //
			var sCompId     = "";
			var sLinkMenuId = "REV3040";

			//fnc_IsNull( nDataSet.GetColumn (0,"MNG_NO") ) return;
			sRecvUser =  nDataSet.GetColumn(0,"MNG_NO")+",";					

			sRecvUser = substr(sRecvUser,0,LENGTH(sRecvUser)-1);

			sParams = "sSendUser="+quote(sSendUser);
			sParams += " sRecvUser="+quote(sRecvUser);
			sParams += " sTitle="+quote(sTitle);
			sParams += " sLinkMenuId="+quote(sLinkMenuId);
			sParams += " sDesc="+quote(sDesc);
			sParams += " sCompId="+quote(sCompId);

			nHandle = Open("common::comSendMessage.xml", sParams, 192, 552, "Title=false");

		break;
	 }
}

function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strText != "") fcSearch();
}
