function fcSaveCheck(obj)
{ 	
	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsEmp)) {
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	for ( var i = 0; i<dsEmp.RowCount(); i++) {
		if( dsEmp.GetColumn(i, "MULT_ESTM_GRP") == null ) {
			fnc_Message(SNIS_RBM_F005, "N");
			return;
		}
	}
	return true;
}

