function fcDataSave() {
	var sServiceName = "rev1080-service:saveEmp";
	var sInDataSet   = "dsEmp=dsEmp:u ";
	var sOutDataSet  = "";		

	fcEstmYearValue();	

	fcd_AddParam("ESTM_YEAR", sEvalYear);	
	fcd_AddParam("ESTM_NUM" , sEvalNum);	
	fcd_AddParam("GRANT"    , "Y");	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

function fcApprSave(){

	var sServiceName = "rev1080-service:saveMultAppr";
	var sInDataSet   = "";
	var sOutDataSet  = "";		

	fcEstmYearValue();	

	fcd_AddParam("ESTM_YEAR", sEvalYear);	
	fcd_AddParam("ESTM_NUM" , sEvalNum);		
	fcd_AddParam("DEPT_CD"  , sDeptCd);
	fcd_AddParam("GRANT"    , "Y");	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);				

}

