	}
}


/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 완료 후
	if ( sServiceName == "rev1080-service:searchEmp" ) {
		fcSearchEnd();
		if(dsEmp.rowcount == 0) return;

		//그룹생성
		if( sGroupClick == "Y" ) {
			sGroupClick = "N";
			fcSetGrp();
		}
	}

	//평가마감여부 조회 완료 후
	if ( sServiceName == "rev2010-service:searchEstmEnd" ) {
		if( dsEstmEnd.GetColumn(0, "ESTM_END_YN") == "Y" ) {
			divBtnLst.btnSave.Enable = false;
			groupBtn.Enable = false;
			ApprBtn.Enable = false;
			grdList.Editable = false;
		} else {
			divBtnLst.btnSave.Enable = true;
			groupBtn.Enable = true;
			ApprBtn.Enable = true;
			grdList.Editable = true;
		}
	}

	if ( sServiceName == "rev1080-service:searchCbDept" ) {
		fcSearchComboCntr();
	}

	if ( sServiceName == "rev1080-service:searchCbCntr" )
	{
		dsCntrItm.InsertRow(0);
		dsCntrItm.SetColumn(0, "CD_NM", "전체");
		choCntrItm.index = 0;

		if (dsEmp.RowCount() == 0) fcSearch();	
	}	
	// 저장 완료 후
	if ( sServiceName == "rev1080-service:saveEmp" ) {
		var ARRAY2_GBL_RET_VALUE = split2(GBL_RET_VALUE,"^","=");
        var sResult = fnc_Array2Value(ARRAY2_GBL_RET_VALUE, "RESULT"); // 결과

        if( sResult == "S") {	//정상적으로 저장이 되었을 떄
            fcApprSave();
        }
	}	

	// 승인관리 완료 후
	if ( sServiceName == "rev1050-service:saveAprvMana" ) {
		fcSearch();		
	}

	// 평가대상선정 완료 후
	if ( sServiceName == "rev1080-service:saveMultAppr" ) {
		fcSearch();		
	}
}

function fcEstmYearValue(obj)
{
	/* 콤보박스에서 값 가져오기 */
    var nCbValue =cbEstmYear.Value;
	var nCbRow = dsEstmYear.SearchRow("CD=="+nCbValue);

	sEvalYear = dsEstmYear.GetColumn(nCbRow,"ESTM_YEAR");
	sEvalNum  = dsEstmYear.GetColumn(nCbRow,"ESTM_NUM");
	sDeptCd   = cboDept.Value;
}

function fcDsOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	switch(obj.id) {
		case "dsEmp" :
			if ( strColumnID = "MULT_ESTM_GRP" ) {
				var nMultEvGrp = dsEmp.GetColumn(nRow, "MULT_ESTM_GRP");

				if ( !fnc_IsNull(varNewValue) && toInteger(varNewValue) <= 0 ) {
					fnc_Message(SNIS_RBM_F003, "N");
					obj.SetColumn(nRow, strColumnID , "");
					grdList.SetFocus();
					return;
				}

				//한 그룹이 10명이 초과되는 것을 막아줌.
				var nCnt = 0;
				var sDeptCd = obj.GetColumn(nRow, "DEPT_CD");
				for(var i=0; i<obj.rowcount; i++) {
					if( obj.GetColumn(i, "MULT_ESTM_GRP") == varNewValue &&
						obj.GetColumn(i, "DEPT_CD")     == sDeptCd ) {
						nCnt++;
					}
				}

				if( !fnc_IsNull(varNewValue) && nCnt > 10 ) {
					fnc_Message(SNIS_RBM_F004, "N");
					obj.SetColumn(nRow, strColumnID, "");
					grdList.SetFocus();
					return;
				}
			}
	}
}

function fcCheckCombo(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcSearchComboDept();
}

//평가대상생성 Check
function fcBtnCheck() {
	//작업중인 데이터 존재 시 return
	/*
	if ( fnc_DatasetChangeCheck(dsEmp) ) {
		fnc_Message(SNIS_COM_1052, "N");
		return false;
	}
	*/

	//다면평가그룹 데이터 없을 시
	if( dsEmp.rowcount == 0 ) {
		fnc_Message(SNIS_COM_1053, "N");
		return false;
	}

	//다면평가그룹 필수항목
	for(var i = 0; i < dsEmp.rowcount; i++) {
		if( fnc_IsNull( dsEmp.GetColumn(i, "MULT_ESTM_GRP")) ) {
			fnc_Message(SNIS_RBM_D001, "N", i+1, "다면평가그룹");
			dsEmp.row = i;
			return false;
		}
	}

	return true;
}

//버튼 Click
function fcBtnOnClick(obj) {
	switch( obj.id ) {
		case "ApprBtn" :
			if ( !fcValid() ) return;
			if ( !fcBtnCheck() ) return;

			if(fnc_Message(SNIS_RBM_F034, "Y")) {  
			    fcDataSave();
			}
			break;
		case "groupBtn" :
			if( dsEmp.rowcount == 0) return;
			if ( !fcValid() ) return;
			fcSearch();
			sGroupClick = "Y";
	}
}

/*************************************************************************************************
                        그룹생성
**************************************************************************************************/

var arrDeptCd;		// 부서배열
var arrItmcd; 		// 보직분야목록
var nGrpMaxNo = 10;	// 그룹max
var nGrpMinNo = 3;	// 그룹min
var nGrpSeq	  = 1;	// 그룹seq

function fcSetGrp()
{
	var nDeptCnt	=0;
	var nItmCnt 	=0;
	var nCnt		=0;
	nGrpSeq = 9999;

	// 그룹조회
	for(var i=0; i< dsEmp.rowcount;i++){
		if( nGrpSeq > ToNumber(dsEmp.GetOrgColumn(i,"MULT_ESTM_GRP"))) {			
			nGrpSeq = ToNumber(dsEmp.GetColumn(i,"MULT_ESTM_GRP"));
		}
	}	


	// 부서추출
	arrDeptCd = array(-1);	
	for(var i=0; i<dsEmp.rowcount;i++){
		if( !fcExitItemCd(arrDeptCd, dsEmp.GetColumn(i,"DEPT_CD")) ){			
			// 존재하지 않으면...
			arrDeptCd[nCnt] = dsEmp.GetColumn(i,"DEPT_CD");
			nCnt++;
		}
	}	

	// 보직분야 추출
	nCnt = 0;
	arrItmcd = array(-1);	
	for(var i=0; i<dsEmp.rowcount;i++){
		if( !fcExitItemCd(arrItmcd, dsEmp.GetColumn(i,"CNTR_ITM_CD")) ){			
			// 존재하지 않으면...
			arrItmcd[nCnt] = dsEmp.GetColumn(i,"CNTR_ITM_CD");
			nCnt++;
		}
	}

	// 부서별 보직분야 추출(이차원배열)
	var nMenCnt=0;
	var n,x;
	var nMod, nVal;
	dsEmp.fireEvent = false;
	dsEmp.Sort("DEPT_CD,CNTR_ITM_CD");
	for(var i=0;i<length(arrDeptCd);i++){
		for( var j=0;j<length(arrItmcd);j++){
			nGrpMaxNo = 10;
			dsEmp.Filter("DEPT_CD="+quote(arrDeptCd[i])+"&&CNTR_ITM_CD="+quote(arrItmcd[j]) );
			nMenCnt = dsEmp.rowcount;	
			if( nMenCnt <= nGrpMaxNo){
				if( nMenCnt < nGrpMinNo ){
					for(var k=0;k<dsEmp.rowcount;k++){
						dsEmp.SetColumn(k,"MULT_ESTM_GRP",0);				
					}
				}else{
					for(var k=0;k<dsEmp.rowcount;k++){
						dsEmp.SetColumn(k,"MULT_ESTM_GRP",nGrpSeq);
					}
					nGrpSeq++;
				}	

			}else{

				var nGrpNo, nGrpCnt;
				nGrpCnt = ceil(nMenCnt / nGrpMaxNo);
				var arrGrpMbrCnt = array(nGrpCnt);
				for( var icnt=0; icnt<nGrpCnt;icnt++) {
					arrGrpMbrCnt[icnt] = 0;
				}	

				var nGrpMbrMax; 
				var nGrpMbrGrpMax;
				nGrpMbrMax = ceil(dsEmp.rowcount / nGrpCnt); 
				nGrpMbrGrpMax = dsEmp.rowcount % nGrpCnt;	// 그룹당 최대인원수 그룹의 숫자

				nGrpNo = nGrpSeq;	
				for( var k=0;k<dsEmp.GetRowCount();k++){
					/* 부서별 임의로 그룹 생성(2011년)
					while( 1==1 ){
						nGrpNo = random(nGrpSeq, nGrpSeq+nGrpCnt);
						if( arrGrpMbrCnt[nGrpNo-nGrpSeq] < nGrpMbrMax ){ //nGrpMaxNo
							arrGrpMbrCnt[nGrpNo-nGrpSeq]++;
							break;
						}
					}
					*/
					/*  부서내 투표소별 그룹 생성(2012년) : 항상 같은 그룹이 생성됨*/
					while( 1==1 ){
						if( arrGrpMbrCnt[nGrpNo-nGrpSeq] < nGrpMbrMax){ //nGrpMaxNo
							arrGrpMbrCnt[nGrpNo-nGrpSeq]++;
							break;
						} else {
							nGrpNo ++;
							if ((nGrpNo-nGrpSeq) == nGrpMbrGrpMax) {		
								nGrpMbrMax = nGrpMbrMax -1;
							}
						}
					}					
					dsEmp.SetColumn(k,"MULT_ESTM_GRP",nGrpNo);
				}
				nGrpSeq += nGrpCnt;		
			}
			dsEmp.UnFilter();
		}	
	}
	dsEmp.fireEvent = true;

}

function fcgetDiv(nData,nDiv)
{
	var nRtn = truncate(nData % nDiv);
	return nRtn;	
}

// 보직분야배열에 이미 존재하는지 체크(true/false)
function fcExitItemCd(arr,strItmCd)
{
	for(var i=0;i<length(arr);i++){
		if( arr[i] == strItmCd ){
			return true;
		}
	}
	return false;
}

function fcChoOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	switch( obj.id ) {
		case "cboDept" :
			fcSearch();
			break;
		case "choCntrItm" :
			fcSearch();
			break;
	}
}

//보직분야 체크
function fcValid() {
	if( fnc_IsNull(choCntrItm.Value) ) {
		return true;
	} else {
		fnc_Message(SNIS_RBM_F052, "N");
		return false;
	}
}
