function fcInit(obj) {
    dsEvEmp.ClearData();  
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    edEmpNmSearch.Text = Trim(edEmpNmSearch.Text);
	fcInit(obj);

	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rev4030-service:searchEvEmp";
    var sInDataSet   = "";
    var sOutDataSet  = "dsEvEmp=dsEvEmp ";     

    fcEstmYearValue();	

    var nEstmYearRow = dsEstmYear.FindRowNF("CD", cboEstmYear.Value);

    var sEvalYear = dsEstmYear.GetColumn(nEstmYearRow  , "ESTM_YEAR");
    var sEvalNum  = dsEstmYear.GetColumn(nEstmYearRow  , "ESTM_NUM");

    //조회조건
    fcd_AddParam("ESTM_YEAR", sEvalYear);	
	fcd_AddParam("ESTM_NUM" , sEvalNum);
	fcd_AddParam("DEPT_CD"  , cboDept.Value);
	fcd_AddParam("EMP_NM"   , edEmpNmSearch.Text);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

//부서코드 조회
function fcSearchComboDept(obj)
{
	dsDept.ClearData();

	var sServiceName = "rev1050-service:searchCbDept";
    var sInDataSet   = "";
    var sOutDataSet  = "dsDept=dsDept dsDeptGrd=dsDept";     

	fcEstmYearValue();	

	fcd_AddParam("ESTM_YEAR", sEvalYear);	
	fcd_AddParam("ESTM_NUM" , sEvalNum);	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

//보직코드 조회
function fcSearchComboWkJob(obj)
{
	dsWorkJob.ClearData();

	var sServiceName = "rev1050-service:searchCbWkJb";
    var sInDataSet   = "";
    var sOutDataSet  = "dsWorkJob=dsWorkJob ";     

	fcEstmYearValue();	

	fcd_AddParam("ESTM_YEAR", sEvalYear);	
	fcd_AddParam("ESTM_NUM", sEvalNum);	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

