/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 완료 후
	if ( sServiceName == "rev4030-service:searchEvEmp" ) {

		//Grid Header 정보 변경
		fcGridHeadInit();	//rid Header 초기화

		var nRow = dsEvalInfo.FindRow("CD", cboEstmYear.Value);

		grdEvEmp.SetCellProp("Head", 5, "text", dsEvalInfo.GetColumn(nRow  , "ESTM_YEAR") + "년");
		grdEvEmp.SetCellProp("Head", 8, "text", dsEvalInfo.GetColumn(nRow  , "ESTM_NUM")  + "차") ;

		if( nRow+1 <= dsEvalInfo.rowcount) {
			grdEvEmp.SetCellProp("Head", 6 ,"text", dsEvalInfo.GetColumn(nRow+1, "ESTM_YEAR") + "년");
			grdEvEmp.SetCellProp("Head", 9 ,"text", dsEvalInfo.GetColumn(nRow+1, "ESTM_NUM")  +"차") ;
		}

		if( nRow+2 <= dsEvalInfo.rowcount) {
			grdEvEmp.SetCellProp("Head", 7 ,"text", dsEvalInfo.GetColumn(nRow+2, "ESTM_YEAR") + "년");
			grdEvEmp.SetCellProp("Head",10 ,"text", dsEvalInfo.GetColumn(nRow+2, "ESTM_NUM")  +"차") ;
		}

		var sFilter = "";
		if( cboGrade.Value == "") {
			dsEvEmp.Filter("");
			return;
		}

		dsEvEmp.FireEvent = false;

		sFilter  = "(GRD_1 != '' OR GRD_2 != '' OR GRD_3 != '')";
		sFilter += " AND (GRD_1 = '' OR GRD_1 = '" + cboGrade.Value + "')";
		sFilter += " AND (GRD_2 = '' OR GRD_2 = '" + cboGrade.Value + "')";
		sFilter += " AND (GRD_3 = '' OR GRD_3 = '" + cboGrade.Value + "')";

		dsEvEmp.Filter(sFilter);
		dsEvEmp.FireEvent = true;		 
	}

	//  부서 콤보 조회 완료 후
	if ( sServiceName == "rev1050-service:searchCbDept" ) {

		dsDept.InsertRow(0);
		dsDept.SetColumn(0, "DEPT_NM", "전체");
		cboDept.index = 0;
	}

	//  보직 콤보 조회 완료 후
	if ( sServiceName == "rev1050-service:searchCbWkJb" ) {
		dsWorkJob.InsertRow(0);
		dsWorkJob.SetColumn(0, "WK_JOB_NM", "전체");
		cboWorkJob.index = 0;	
	}
}

