//초기화
function fcGridHeadInit() {
	grdEvEmp.SetCellProp("Head", 5 , "text", "");
    grdEvEmp.SetCellProp("Head", 6 , "text", "");
    grdEvEmp.SetCellProp("Head", 7 , "text", "");
    grdEvEmp.SetCellProp("Head", 8 , "text", "") ;
    grdEvEmp.SetCellProp("Head", 9 , "text", "") ;
    grdEvEmp.SetCellProp("Head", 10, "text", "") ;
}

function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	switch(obj.id) {
		case "cboEstmYear" :
			fcCheckCombo();
		break;
	}
}
