function fcHelpDialoge(obj,nX,nY) {

}

/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 완료 후
	if ( sServiceName == "rev4040-service:searchEvEmp" ) {
		 if (sEvalYear == "2011") {
			grdEvEmp.SetCellProp("head", 1, "text", "보직분야");
		} else {
			grdEvEmp.SetCellProp("head", 1, "text", "부서그룹");
		}
	}

	//  부서 콤보 조회 완료 후
	if ( sServiceName == "rev1050-service:searchCbDept" ) {
		dsDept.InsertRow(0);
		dsDept.SetColumn(0, "DEPT_NM", "전체");
		cboDept.index = 0;
	}

	//  보직분야 조회 완료 후
	if ( sServiceName == "rev1080-service:searchCbCntr" ) {
		dsCntrItm.InsertRow(0);
		dsCntrItm.SetColumn(0, "CD_NM", "전체");
		cboCntrItmCd.index = 0;
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcEdOnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam) {

	switch( obj.id ) {
		case "edEmpNmSearch" :
			if ( nChar == ENTER_KEY )	fcSearch();
			break;
	}
}
/*-------------------------------------------------------------------------------------------------+
>>  콤보박스에서 값 가져오기
+-------------------------------------------------------------------------------------------------*/
function fcEstmYearValue(obj)
{
    var nCbValue = cboEstmYear.Value;
	var nCbRow   = dsEstmYear.SearchRow("CD=="+nCbValue);

	sEvalYear = dsEstmYear.GetColumn(nCbRow,"ESTM_YEAR");
	sEvalNum  = dsEstmYear.GetColumn(nCbRow,"ESTM_NUM");
}

//부서, 보직 조회
function fcCheckCombo() {
	fcSearchComboDept();
	fcSearchComboCntr();
}

// Combo변경시
function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	switch(obj.id) {
		case "cboEstmYear" :
			fcCheckCombo();
		break;
	}
}
