	return true;
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {

	if ( !fcSaveCheck() ) return;

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	sServiceName = "rsm1010-service:multiLISFileSave";
	sInDataSet   = "dsLISFile=dsLISFile:a ";
	sOutDataSet  = "";

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

