/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

   if ( sServiceName == "common-service:searchCode" ) {
		cboStndYear.Value = dscond.GetColumn(0, "STND_YEAR");
		cboTms.Value = dscond.GetColumn(0, "TMS");
		cboDayOrd.Value = dscond.GetColumn(0, "DAY_ORD");
		rdoMeetCd.Value  = dscond.GetColumn(0, "CUR_MEET_CD");		
   } else if ( sServiceName == "rsm1040-service:searchTms" ) { // TMS 값 검색

		if(dsTms.GetRowCount()==0)
		{		
			dsTms.SetColumn(0,"TMS","");
			dsTms.SetColumn(0,"TMS_DESC","없음");
			cboTms.Index=0;
		}
		else
		{
			cboTms.Value = dsTms.GetColumn(0,"MAX_TMS");		
		}
		fcSearchDayOrd();
		return;
	} else if ( sServiceName == "rsm1040-service:searchDayOrd" ) {		

		if(dsDayOrd.GetRowCount()==0)
		{
			dsDayOrd.AddRow();
			dsDayOrd.SetColumn(0,"DAY_ORD","");
			dsDayOrd.SetColumn(0,"DAY_DESC","없음");
			cboDayOrd.Index=0;
		}
		else
		{
			cboDayOrd.Value = dsDayOrd.GetColumn(0,"MAX_DAY_ORD");
			RACE_DAY		= dsDayOrd.GetColumn(0,"RACE_DAY"); // 경주일
		}
		//fcSearch();
		return;
	}


	// 조회 완료 후
	if ( sServiceName == "rsm1070-service:searchRefund" ) {


	}


	//  저장 완료 후
	if ( sServiceName == "rsm1070-service:saveRefund" ) {

		fcSearch();
	}
}


function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{


	switch ( obj.ID ) {

		case "cboStndYear" : // 기준년도 변경
			//fcSearchTms();
			break;

		case "cboTms" :		// 회차 변경
			//fcSearchDayOrd();		
			break;

		case "cboDayOrd" :	// 일차 변경
			//fcSearch();
			break;


	}


}



/*-------------------------------------------------------------------------------------------------+
>> dataset  OnLoadCompleted  이벤트시 조회되도록 한다.
+-------------------------------------------------------------------------------------------------*/
function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	switch ( obj.id ) {
		case "dsBrncCd" :
			dsBrncCd.InsertRow(0);
			dsBrncCd.SetColumn(0, "CD_NM", "전체");
			cboBrncCd.index = 0;
			break;

	}

}



/*-------------------------------------------------------------------------------------------------+
>>  경정 년도별 가까운 회차
+-------------------------------------------------------------------------------------------------*/
function fcSearchTms(obj) {

	var sServiceName = "rsm1040-service:searchTms"; // 최근 경주 결과 날짜
    var sInDataSet   = "";
    var sOutDataSet  = "dsTms=dsTms ";

	dsTms.ClearData();
	fcd_AddParam("MEET_CD", rdoMeetCd.Value);		// 개최지
	fcd_AddParam("STND_YEAR", cboStndYear.Value);
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}

/*-------------------------------------------------------------------------------------------------+
>>  경정 년도별 가까운 회차별 일차
+-------------------------------------------------------------------------------------------------*/
function fcSearchDayOrd(obj) {

	var sServiceName = "rsm1040-service:searchDayOrd"; // 최근 경주 결과 날짜

    var sInDataSet   = "";
    var sOutDataSet  = "dsDayOrd=dsDayOrd ";

	dsDayOrd.ClearData();
	fcd_AddParam("MEET_CD", rdoMeetCd.Value);		// 개최지
	fcd_AddParam("STND_YEAR", cboStndYear.Value);
	fcd_AddParam("TMS", cboTms.Value);
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}
