	var sRetValue = decode(GBL_RET_VALUE,null,'',GBL_RET_VALUE);
	if(sRetValue!='')
	{		
		// 이전 페이지에서 파라미터 받음	
		ARRAY2_GBL_RET_VALUE = split2(GBL_RET_VALUE," ","=");
		GBL_RET_VALUE="";
		calRaceDay.Value  = fcArray2Value(ARRAY2_GBL_RET_VALUE,"RACE_DAY");
		rdoMeetCd.Value  = fcArray2Value(ARRAY2_GBL_RET_VALUE,"MEET_CD");

		TMS       = fnc_Array2Value(ARRAY2_GBL_RET_VALUE,"TMS");
		DAY_ORD   = fnc_Array2Value(ARRAY2_GBL_RET_VALUE,"DAY_ORD");
		RACE_YOIL = fnc_Array2Value(ARRAY2_GBL_RET_VALUE,"RACE_YOIL");

		fcSearchInfo();

	}
}


/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	//Return Key Press時 Next Component focus
	if ( nChar == ENTER_KEY ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}

}

