function fcSearchInfo(obj) {

    if ( !fcSearchCheck(obj) ) return;

    // 이전 페이지에서 파라미터 받음

	COMM_NO	  = fcArray2Value(ARRAY2_GBL_RET_VALUE,"COMM_NO");
	DIV_NO	  = decode(fnc_Array2Value(ARRAY2_GBL_RET_VALUE,"DIV_NO"),null,"",fnc_Array2Value(ARRAY2_GBL_RET_VALUE,"DIV_NO")) ;
	BRNCH_NM  = fcArray2Value(ARRAY2_GBL_RET_VALUE,"BRNCH_NM");
	SELL_CD   = fcArray2Value(ARRAY2_GBL_RET_VALUE,"SELL_CD");
	RACE_YOIL = fcArray2Value(ARRAY2_GBL_RET_VALUE,"RACE_YOIL");


	// 화요경정인 경우 전주 개념이 이전 화요일 경주를 의미함		
	if (rdoMeetCd.Value == "003" && RACE_YOIL = "TUE") {
		fcd_AddParam("TUE_MBR_YN", "Y");	
	} else {
		fcd_AddParam("TUE_MBR_YN", "N");
	}

    var sServiceName = "rsm2011-service:searchSalesViewDiv";  //  지점별 매출액 비교 조회

	var sInDataSet   = "";
	var sOutDataSet  = "dsSalesViewDiv=dsSalesViewDiv ";

	if( calRaceDay.Value == fcd_GetSysdate("YYYYMMDD"))
	{
		sServiceName = "rsm2011-service:searchSalesViewDivToday";  // 지점별 매출액 비교 조회 오늘
		sOutDataSet  += "dsDivSales=dsDivSales ";     
	}
	else
	{
		sOutDataSet  += "dsDiv=dsDiv ";     
	}


	// 매출현황 파라미터
	fcd_AddParam("RACE_DAY", calRaceDay.Value );  
	//fcd_AddParam("MEET_CD", rdoMeetCd.Value );  
	fcd_AddParam("COMM_NO", COMM_NO);        
	fcd_AddParam("DIV_NO", DIV_NO);        
	fcd_AddParam("BRNCH_NM", BRNCH_NM );    
	fcd_AddParam("SELL_CD",  SELL_CD );    

	fcd_AddParam("MD", "1"); 

	var nTms = parseInt(TMS);
	var nDayOrd = parseInt(DAY_ORD);

	var sBrnchNmTitle = BRNCH_NM+" "+nTms+"회 "+nDayOrd+" 일차";

	grdSalesViewDiv.SetCellProp("head",0,"text", "("+BRNCH_NM+") 매출액 비교");

	if (calRaceDay.Value == "20140808" || calRaceDay.Value == "20140809" || calRaceDay.Value == "20140810") {
		fcd_AddParam("MEET_CD",  "001");        
		stBrncInfo.text = "※ 세계주니어선수권대회기간에는 교차매출에 대한 지점별 매출액입니다.";		
	} else if (calRaceDay.Value == "20160923" || calRaceDay.Value == "20160924" || calRaceDay.Value == "20160925") {
		fcd_AddParam("MEET_CD",  "001");        
		stBrncInfo.text = "※ 경륜 휴장기간 교차매출에 대한 지점별 매출액입니다.";		
	} 
	else {
		fcd_AddParam("MEET_CD",  rdoMeetCd.Value);        
		stBrncInfo.text = "";
	}


	if(sBrnchNmTitle.length>15)
	{		
		grdSalesViewDiv.SetColProp(4,"width",200);
	}
	else
	{
		grdSalesViewDiv.SetColProp(4,"width",140);
	}

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",true);


}

// 투표소별 내용 조회
function fcSearchAmt(obj) {

	var sServiceName = "rsm2011-service:searchAmt";
	if( calRaceDay.Value == fcd_GetSysdate("YYYYMMDD"))
	{
		sServiceName = "rsm2011-service:searchAmtToday";  // 지점별 매출액 비교 조회 오늘
	}

    var sInDataSet   = "";
    var sOutDataSet  = "dsAmt=dsAmt ";
    var COMM_NO= dsDiv.GetColumn(dsDiv.row , "COMM_NO");
    var SELL_CD= dsDiv.GetColumn(dsDiv.row , "SELL_CD");
    var DIV_NO = dsDiv.GetColumn(dsDiv.row , "DIV_NO");


	// 매출현황 파라미터	
	fcd_AddParam("MEET_CD",   rdoMeetCd.Value );  
	fcd_AddParam("SELL_CD",   SELL_CD );  
	fcd_AddParam("COMM_NO",   COMM_NO);        
	fcd_AddParam("DIV_NO",    DIV_NO);        
	fcd_AddParam("RACE_DAY",  calRaceDay.Value ); 
	fcd_AddParam("RACE_YOIL", RACE_YOIL );  	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


