/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {

	var sExcelList = "grdSalesViewDiv:3"; 

	if( calRaceDay.Value == fcd_GetSysdate("YYYYMMDD"))
	{
		if(dsDivSales.rowcount>0)
		{
			sExcelList += "|grdDivSales:2"; // :숫자는 + head +summary+ 하단 띄우고 싶은 사이 간격
		}
	}
	else
	{
		if(dsAmt.rowcount>0)
		{
			sExcelList += "|grdAmt:2"; // :숫자는 + head +summary+ 하단 띄우고 싶은 사이 간격
		}
	}

    fnc_OpenMultiExcelExport(sExcelList,"매출액비교");

}

