/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

    if(sServiceName = "rsm2010-service:searchMeetCd")
    {
		rdoMeetCd.Value = dsMeetCd.GetColumn(0,"CD");			
		if(dsMeetCd.rowcount > 0) // 경주일이 있을 경우만 검색
		{
			fcSearchInfo();
		}
		else
		{
			fcInit();
		}
		return;
    }

    if ( sServiceName == "rsm2011-service:searchSalesViewDiv" || sServiceName == "rsm2011-service:searchSalesViewDivToday" )
    {
		stcomm1.Visible = true;
		if(sServiceName == "rsm2011-service:searchSalesViewDivToday") {
			grdDiv.Visible = false;
			grdAmt.Visible = false;
			grdDivSales.Visible = true;
		} else {
			if (COMM_NO == "06") {
				grdDiv.Visible = false;
				grdAmt.Visible = false;
				grdDivSales.Visible = false;
				stcomm1.Visible = false;
			} else {
				grdDiv.Visible = true;
				grdAmt.Visible = true;
				grdDivSales.Visible = false;
			}
		}

		if(sServiceName == "rsm2011-service:searchSalesViewDivToday" && COMM_NO == "06") {
		    stcomm1.text = "※ 당일 그린카드매출액의 투표소별 현황을 조회되지 않습니다.(일일 정산이후 조회 가능합니다.)";
		} else {
		    stcomm1.text = "※ 전주의 투표소 현황이 다를 경우 투표소별 매출액의 전주 대비 합계금액은  매출현황의 총합계금액과 오차가 발생할 수  있습니다.";
		}
		// 요일별로 비교 안내 표시
		if (dsSalesViewDiv.RowCount() > 0) {
			if (dsSalesViewDiv.GetColumn(1, "BEFORE_DIV_TOTAL")	== 0)      stCompare.visible = true;
			else if (dsSalesViewDiv.GetColumn(1, "LAST_DIV_TOTAL") == 0) stCompare.visible = true;
			else stCompare.visible = false;
		}
		fcGridPosition();

		fcSearchAmt();	//투표소별 매출액

		return;
	}


}



// 2차원 배열의 변수 값 확인
function fcArray2Value(arr2Param,strParam)
{
	var strResult = "";
	for(var i=0;i<arr2Param.length;i++)
	{	
		for(var j=0;j<arr2Param[i].length;j++)
		{
			if(arr2Param[i][0]==strParam)
			{
				strResult = arr2Param[i][1];
			}
		}
	}
	return strResult;
}


function fcOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{


	switch ( obj.ID ) {

		case "rdoMeetCd" : // 기준년도 변경
			var strParam = "RACE_DAY="+calRaceDay.Value;
			strParam +=" MEET_CD="+rdoMeetCd.Value;			
			GBL_RET_VALUE=strParam; // 값 생성	
			global.FrameBottom.fcOpenMdiForm("RSM2010", "매출현황", "frm_rsm::RSM2010.xml");
			break;

		case "rdoAutoOPT" : // 수동 자동 구분
			if(strCode=="2")
			{
				cboTimeBox.Enable = true;
				SetTimer(1, parseInt(cboTimeBox.Value) *60 * 1000 ); // Timer구동
			}
			else
			{
				cboTimeBox.Enable = false;
				KillTimer(1); // Timer해제
			}

			break;

		case "cboTimeBox":
			KillTimer(1); // Timer해제
			SetTimer(1, parseInt(strCode) *60 * 1000 ); // Timer구동	
			break;
	}



//	Close();	
}

/*-------------------------------------------------------------------------------------------------+
>>  버튼 클릭 이벤트
+-------------------------------------------------------------------------------------------------*/

function fcBtnOnClick(obj,nX,nY)
{
	switch ( obj.id )
    {
		case "btnFullScreen" :
			var sFormId 	= "frm_rsm::RSM2011P.xml";


			var strParam = "RACE_DAY="+calRaceDay.Value;
			strParam +=" MEET_CD="+rdoMeetCd.Value;
			strParam +=" COMM_NO="+COMM_NO;
			strParam +=" DIV_NO="+DIV_NO;
			strParam +=" BRNCH_NM="+BRNCH_NM;
			strParam +=" TMS="+TMS;
			strParam +=" DAY_ORD="+DAY_ORD;


			GBL_RET_VALUE=strParam; // 값 생성	

			var nCXScreen	= GetDeviceInfo("CXScreen");			
			var nCYScreen	= GetDeviceInfo("CYScreen");

			Dialog(sFormId, GBL_RET_VALUE,nCXScreen,nCYScreen, "Title=true CloseFlag=true","1","1");
		break;		
	}
}


