function fcOnChar(obj,strPreText,nChar,strPostText,nLLParam,nHLParam)
{
	switch ( obj.ID ) {		
		// 경주일자에서 ENTER_KEY 눌렀을 경우
		case "calRaceDay" :	
			if ( nChar == ENTER_KEY ) {
				fcSearch();
				return;
			}
		break;

	}
}


// 경륜 경정에 따른 그리드높이와, 위치 이동
function fcGridPosition()
{
	var MEET_CD	= rdoMeetCd.Value;	
	if(MEET_CD=="003")
	{
		grdSalesViewDiv.Height = 345;
		grdDiv.top = 475;
		grdAmt.top =475;
		grdDivSales.top = 475;
		stcomm1.Top = 452;
	}
	else
	{
		grdSalesViewDiv.Height = 432;
		grdDiv.top = 568;
		grdAmt.top = 568;
		grdDivSales.top =568;
		stcomm1.Top = 544;
	}
}

function fcChkTmsSpcl(sTitle, gbn)
{
	var sTms = substr(sTitle, 0, IndexOf(sTitle, "회"));
	if (toNumber(TMS) - gbn != toNumber(sTms))	return true;
	return false;
}
