function fcToExcel(obj) {

	var sExcelList = "Tab0.tab1.grdBFYYCompare:2"; 
		sExcelList += "|Tab0.tab1.grdRaceSales:5"; 
		sExcelList += "|Tab0.tab1.grdEntPrsnNum:2"; // :숫자는 + head +summary+ 하단 띄우고 싶은 사이 간격
		sExcelList += "|Tab0.tab1.grdSalesGoal:2"; // :숫자는 + head +summary+ 하단 띄우고 싶은 사이 간격
		sExcelList += "|Tab0.tab2.grdOutSales:6"; // :숫자는 + head +summary+ 하단 띄우고 싶은 사이 간격
		sExcelList += "|Tab0.tab2.grdOutEntPrsnNum:2"; // :숫자는 + head +summary+ 하단 띄우고 싶은 사이 간격
    if (tab0.tabindex == 2) sExcelList = "Tab0.tab3.grdTueSales:2"; 
    fnc_OpenMultiExcelExport(sExcelList,"경정개최결과");
}

