/*-------------------------------------------------------------------------------------------------+
>>  인쇄
+-------------------------------------------------------------------------------------------------*/
function fcPrint(){
	var sFormId 	= "";
	var sArgss 		= "";
	var arrReturn   = array(2);
	var sRptfile    = "";
	vReturn = "";
	sFormId = "frm_rsm::RSM2020P1.xml";
	sArgs = "";	

	arrReturn = Dialog(sFormId, sArgss, 240, 128, "");

	if (fnc_IsNull(vReturn) == false )
	{
		var today_yn = "N";
		if(RACE_DAY == fcd_GetSysdate("YYYYMMDD")) today_yn = "Y";

		//데이터셋을 초기화 한다.
		ds_reportParam.DeleteAll();
		var nRnum;

		// 필드 값을 셋팅한다.
		var paramField = [
								"STND_YEAR"							// 발매년도
							,	"MEET_CD"							// 시행처코드
							,	"TMS"								// 회차
							,	"DAY_ORD"							// 일자
							,	"TODAY_YN"							// 당일 여부
						 ];

		// 값을 셋팅한다.
		var paramValue = [
								cboStndYear.Value
							,	"003"
							,	cboTms.Value
							,	cboDayOrd.Value
							,	today_yn
						 ];


		var rowCnt = paramField.length;	
		for(var row = 0 ; row < rowCnt; row++)
		{
			nRnum = ds_reportParam.AppendRow();
			ds_reportParam.SetColumn(nRnum, "PARAM_NM", paramField[row]);
			ds_reportParam.SetColumn(nRnum, "PARAM_VAL",paramValue[row]);
		}

		/* 레포트 배율 추가 - 시작 */
		iRow = ds_reportParam.AddRow();

		// Default Scale 파라미터명 추가(고정)
		ds_reportParam.SetColumn(iRow, "PARAM_NM", "DEFAULT_SCALE");
		ds_reportParam.SetColumn(iRow, "PARAM_VAL", "1");

		if ( vReturn = "1" ) {	

			sRptfile = "rsm2020";			

		} else if ( vReturn = "2" ) {		
			if (cboDayOrd.Value == "1") sRptfile = "rsm2020_1";			
			else 						sRptfile = "rsm2020_2";			
		} else if ( vReturn = "3" ) {		
			sRptfile = "rsm2020_3";			
		}

		//레포트 트랙잭션을 태운다.
		fnc_ReportPreView(sRptfile, ds_reportParam.ID, "N");			
	}
}

