/*-------------------------------------------------------------------------------------------------+
>>  combobox, radio 바뀔 때 이벤트
+-------------------------------------------------------------------------------------------------*/
function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{


	switch ( obj.ID ) {

		case "cboStndYear" : // 기준년도 변경
			fcSearchTms();
			break;

		case "cboTms" :		// 회차 변경
			fcSearchDayOrd();		
			break;

		case "cboDayOrd" :	// 일차 변경
			fcSearch();
			break;

		case "rdoAutoOPT" : // 수동 자동 구분
			if(strCode=="2")
			{
				cboTimeBox.Enable = true;
				SetTimer(1, parseInt(cboTimeBox.Value) *60 * 1000 ); // Timer구동
			}
			else
			{
				cboTimeBox.Enable = false;
				KillTimer(1); // Timer해제
			}

			break;

		case "cboTimeBox":
			KillTimer(1); // Timer해제
			SetTimer(1, parseInt(strCode) *60 * 1000 ); // Timer구동	
			break;
	}


}




