/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/

var ARRAY2_GBL_RET_VALUE = Array();	// 이전페이지에서 넘어온 변수
var MEET_CD = "";
var RACE_DAY = "";
var RACE_YOIL = "";
function fcFormLoadSetting(obj) {  

    sBUTTONLIST   = 'TTFFTTT';     // T초기화,T조회,F저장,F삭제,T엑셀,F인쇄,T닫기

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	//공통코드 조회
	fcd_GetComboDs(dsInComCd);

	fcd_SetStndYear(dsStndYear,  dsCond.GetColumn(0,"MIN_STND_YEAR"), substr(fcd_GetSysdate("YYYYMMDD"), 0, 4)); 

	var sRetValue = decode(GBL_RET_VALUE,null,'',GBL_RET_VALUE);
	if(sRetValue!='')
	{		
		// 이전 페이지에서 파라미터 받음	
		ARRAY2_GBL_RET_VALUE = split2(GBL_RET_VALUE," ","=");
		GBL_RET_VALUE="";
		cboStndYear.Value = fcArray2Value(ARRAY2_GBL_RET_VALUE,"STND_YEAR");		
		MEET_CD	= fcArray2Value(ARRAY2_GBL_RET_VALUE,"MEET_CD");
		RACE_DAY 		 = fcArray2Value(ARRAY2_GBL_RET_VALUE,"RACE_DAY");
		RACE_YOIL 		 = fcArray2Value(ARRAY2_GBL_RET_VALUE,"RACE_YOIL");
		fcSearchRace();
	}

	// 화면그리드 리사이즈
	fcSetResize(this,this.width);
}

