function fcSearchRace(obj) {
	fcSearchMaxRacerCnt(obj);	// 당일 최대출전선수 조회

    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rsm2031-service:searchRaceInfo"; // 

	if(RACE_DAY==fcd_GetSysdate("YYYYMMDD"))
	{
		sServiceName = "rsm2031-service:searchRaceInfoToday"; 
	}

    var sInDataSet   = "";
    var sOutDataSet  = "dsRaceInfo=dsRaceInfo ";
		sOutDataSet  += "dsTmsTotal=dsTmsTotal ";
		sOutDataSet  += "dsRaceResult=dsRaceResult ";
		sOutDataSet  += "dsBrncSales=dsBrncSales ";
		sOutDataSet  += "dsBrncSales2=dsBrncSales2 ";
		sOutDataSet  += "dsBrncEntPrsnNum=dsBrncEntPrsnNum ";
		sOutDataSet  += "dsEtc=dsEtc ";
		sOutDataSet  += "dsRaceYoil=dsRaceYoil ";

    // TMS ,DAY_ORD	전페이지 파라미터

	var MEET_CD 	= fcArray2Value(ARRAY2_GBL_RET_VALUE,"MEET_CD");
    var STND_YEAR	= fcArray2Value(ARRAY2_GBL_RET_VALUE,"STND_YEAR");
    var TMS			= fcArray2Value(ARRAY2_GBL_RET_VALUE,"TMS");
    var DAY_ORD		= fcArray2Value(ARRAY2_GBL_RET_VALUE,"DAY_ORD");
    var RACE_DAY	= fcArray2Value(ARRAY2_GBL_RET_VALUE,"RACE_DAY");
    var RACE_YOIL	= fcArray2Value(ARRAY2_GBL_RET_VALUE,"RACE_YOIL");


	fcd_AddParam("MEET_CD", MEET_CD);	// 경륜 구분 코드
	fcd_AddParam("STND_YEAR", STND_YEAR);	// 기준년도
	fcd_AddParam("TMS", TMS);						// 회차
	fcd_AddParam("DAY_ORD", DAY_ORD);				// 일차
	fcd_AddParam("RACE_DAY", RACE_DAY);				// 경주일
	fcd_AddParam("RACE_YOIL", RACE_YOIL);				// 경주요일

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}


function fcSearchMaxRacerCnt(obj) {

	var sServiceName = "rsm2031-service:searchMaxRacerCnt"; // 
	var sInDataSet   = "";
    var sOutDataSet  = "dsMaxRacerCnt=dsMaxRacerCnt ";

    // TMS ,DAY_ORD	전페이지 파라미터
	var MEET_CD 	= fcArray2Value(ARRAY2_GBL_RET_VALUE,"MEET_CD");
    var STND_YEAR	= fcArray2Value(ARRAY2_GBL_RET_VALUE,"STND_YEAR");
    var TMS			= fcArray2Value(ARRAY2_GBL_RET_VALUE,"TMS");
    var DAY_ORD		= fcArray2Value(ARRAY2_GBL_RET_VALUE,"DAY_ORD");
    var RACE_DAY	= fcArray2Value(ARRAY2_GBL_RET_VALUE,"RACE_DAY");    

	fcd_AddParam("MEET_CD", MEET_CD);	// 경륜 구분 코드
	fcd_AddParam("STND_YEAR", STND_YEAR);	// 기준년도
	fcd_AddParam("TMS", TMS);						// 회차
	fcd_AddParam("DAY_ORD", DAY_ORD);				// 일차
	fcd_AddParam("RACE_DAY", RACE_DAY);				// 경주일

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);	
}



