/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

    if (sServiceName == "rsm2031-service:searchMaxRacerCnt") {
		if (dsMaxRacerCnt.GetColumn(0, "RACER_MAX_CNT") > 7) {
			grdRaceResult.SetColProp(5,"Width","40");
		}
		return;
    }
	if ( sServiceName != "" ) 
	{		
		var RACE_DAY = dsRaceInfo.GetColumn(0,"RACE_DAY");
		var YEAR = substr(RACE_DAY,0,4);
		var MM = substr(RACE_DAY,4,2);
		var DD = substr(RACE_DAY,6,2);
		stRaceInfo1.Text = dsRaceInfo.GetColumn(0,"MEET_NM")+" 제"+dsRaceInfo.GetColumn(0,"TMS")+"회차 "+dsRaceInfo.GetColumn(0,"DAY_ORD")+"일차 ("+YEAR+"년 "+MM+"월 "+DD+"일)";	
		stRaceInfo.Text = dsRaceInfo.GetColumn(0,"MEET_NM")+"개최결과";
		fcBrncSales(); // 지점별 매출 현황 값 생성
		fcBrncSales2(); // 지점별 매출 현황(그린카드 포함) 값 생성
		fcVisibleColumn();	// 나타나는 요일 값만 보여줌
		grdTmsTotal.SetFocus();
		return;
    }


}


/*-------------------------------------------------------------------------------------------------+
>>  함수 정의
+-------------------------------------------------------------------------------------------------*/

// 2차원 배열의 변수 값 확인
function fcArray2Value(arr2Param,strParam)
{
	var strResult = "";
	for(var i=0;i<arr2Param.length;i++)
	{	
		for(var j=0;j<arr2Param[i].length;j++)
		{
			if(arr2Param[i][0]==strParam)
			{
				strResult = arr2Param[i][1];
			}
		}
	}
	return strResult;
}


/*-------------------------------------------------------------------------------------------------+
>>  헤더값 width 조절 함
+-------------------------------------------------------------------------------------------------*/

function fcVisibleColumn()
{	
	// 값을 줄이기 전에 먼저 해당 컬럼들을 초기화 시킴
	//for(var j=0;j<dsTempYoil.rowcount;j++)
	for(var j=0;j<10;j++)
	{	
		var nColIdx = parseInt(dsTempYoil.GetColumn(j,"CD"));
		grdTmsTotal.SetColProp(nColIdx,"width",200);

	}

	// DATASET 초기화
	dsTempYoil.ClearData();
	dsTempYoil.Copy(dsHeadYoil);

	var sFilter = "";
	for(var i=0;i<dsTempYoil.rowcount;i++)
	{
		for(var j=0;j<dsRaceYoil.rowcount;j++)
		{
			if(dsRaceYoil.GetColumn(j,"RACE_YOIL")==dsTempYoil.GetColumn(i,"CD_NM"))
			{	
				if(sFilter=="")
				{
					sFilter ="(CD_NM!='"+dsTempYoil.GetColumn(i,"CD_NM")+"')";
				}
				else
				{
					sFilter +="&&(CD_NM!='"+dsTempYoil.GetColumn(i,"CD_NM")+"')";
				}

			}
		}
	}

	dsTempYoil.Filter(sFilter);

	// 비어있는 컬럼 width 값을 0 으로 만듦
	for(var j=0;j<dsTempYoil.rowcount;j++)
	{
		var nColIdx = parseInt(dsTempYoil.GetColumn(j,"CD"));
		grdTmsTotal.SetColProp(nColIdx,"width",0);
	}
}


// 특정콤포넌트(그리드) 리사이즈
function fcSetResize(obj,nX)
{	// 해당화면은 기준사이즈보다 Height가 크기때문에 스크롤바 생성발생.그에 따라 그리드사이즈 조정
	var arrObj = array(-1);
	arrObj[0] = "grdTmsTotal";
	arrObj[1] = "grdRaceResult";
	arrObj[2] = "grdBrncSales";
	arrObj[3] = "grdBrncSales2";
	arrObj[4] = "grdBrncEntPrsnNum";
//	arrObj[4] = "grdBrncEntPrsnNum";

	var nWidth = nX-28;
	if( nX <= 820){
		for( var i=0; i<length(arrObj); i++){
			if( i==0){
				object(arrObj[i]).Autofit = true;	
			}else{
				object(arrObj[i]).Autofit = false;		

			}
			object(arrObj[i]).width = 780;
		}
		Shape1.Width 	= 780;	
		divBtnLst.Left 	= 431;			
		Static0.Left 	= 705;		
		taEtc.Right  	= 788;	
		return;
	}

	for( var i=0; i<length(arrObj); i++){

		object(arrObj[i]).right = nWidth;
		if( i==0){
			object(arrObj[i]).Autofit = true;
		}else if(i==2 || i==3 || i==4){
			if( nX < decode(i,4,800,840)){
				object(arrObj[i]).Autofit = false;		
			}else {
				object(arrObj[i]).Autofit = true;		
			}
		}else{
			if( nX < 900){
				object(arrObj[i]).Autofit = false;		
			}else {
				object(arrObj[i]).Autofit = true;		
			}
		}

	}	

	// 공통
	Shape1.Width 	= nWidth-8;
	divBtnLst.Left 	= this.width - 405;	
	Static0.Left 	= this.width - 111;	
	taEtc.Right  	= nWidth-8;
}

// 해당 폼사이즈 변경시
function fcOnSize(obj,nCx,nCy,nState)
{
	fcSetResize(obj,nCx);
}

// 경주별 현황 상세 보기
function fcOnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{	
	switch ( obj.id )
    {
		case "grdRaceResult" :
			if(nCell=='0')
			{
				var sFormId 	= "frm_rsm::RSM2031P.xml";
				var sArgs 		= "";		

				var sParam = " MEET_CD="+dsRaceResult.GetColumn(nRow,"MEET_CD");
				sParam += " STND_YEAR="+cboStndYear.Value;
				sParam += " TMS="+dsRaceResult.GetColumn(nRow,"TMS");
				sParam += " DAY_ORD="+dsRaceResult.GetColumn(nRow,"DAY_ORD");
				sParam += " RACE_DAY="+RACE_DAY;
				sParam += " RACE_NO="+dsRaceResult.GetColumn(nRow,"RACE_NO");

				GBL_RET_VALUE=sParam; // 값 생성	

				Dialog(sFormId, GBL_RET_VALUE,704,344, "Title=true CloseFlag=true","1","1");
			}
		break;
	}

}

// 지점별 매출 현황
function fcBrncSales()
{
	// 첫번째 행	
	dsBrncSalesTmp.SetColumn(1,"BONGJANG",dsBrncSales.GetColumn(0,"BONGJANG"));	// 본장
	dsBrncSalesTmp.SetColumn(1,"JANG11",dsBrncSales.GetColumn(0,"JANG11"));		// 수원
	dsBrncSalesTmp.SetColumn(1,"JANG12",dsBrncSales.GetColumn(0,"JANG12"));		// 상봉
	dsBrncSalesTmp.SetColumn(1,"JANG13",dsBrncSales.GetColumn(0,"JANG13"));		// 일산
	dsBrncSalesTmp.SetColumn(1,"JANG14",dsBrncSales.GetColumn(0,"JANG14"));		// 분당
	dsBrncSalesTmp.SetColumn(1,"JANG15",dsBrncSales.GetColumn(0,"JANG15"));		// 동대문
	dsBrncSalesTmp.SetColumn(1,"JANG16",dsBrncSales.GetColumn(0,"JANG16"));		// 장안
	dsBrncSalesTmp.SetColumn(1,"JANG17",dsBrncSales.GetColumn(0,"JANG17"));		// 산본
	dsBrncSalesTmp.SetColumn(1,"JANG18",dsBrncSales.GetColumn(0,"JANG18"));		// 부천
	dsBrncSalesTmp.SetColumn(1,"JANG19",dsBrncSales.GetColumn(0,"JANG19"));		// 관악
	dsBrncSalesTmp.SetColumn(1,"JANG20",dsBrncSales.GetColumn(0,"JANG20"));		// 길음

	// 두번째 행	
	dsBrncSalesTmp.SetColumn(3,"BONGJANG",dsBrncSales.GetColumn(0,"JANG21"));	// 당산
	dsBrncSalesTmp.SetColumn(3,"JANG11",dsBrncSales.GetColumn(0,"JANG22"));		// 유성
	dsBrncSalesTmp.SetColumn(3,"JANG12",dsBrncSales.GetColumn(0,"JANG23"));		// 인천
	dsBrncSalesTmp.SetColumn(3,"JANG13",dsBrncSales.GetColumn(0,"JANG24"));		// 시흥
	dsBrncSalesTmp.SetColumn(3,"JANG14",dsBrncSales.GetColumn(0,"JANG25"));		// 논현
	dsBrncSalesTmp.SetColumn(3,"JANG15",dsBrncSales.GetColumn(0,"JANG26"));		// 천안
	dsBrncSalesTmp.SetColumn(3,"JANG16",dsBrncSales.GetColumn(0,"JANG27"));		// 올림픽
	dsBrncSalesTmp.SetColumn(3,"JANG17",dsBrncSales.GetColumn(0,"JANG28"));		// 의정부
	dsBrncSalesTmp.SetColumn(3,"JANG18",dsBrncSales.GetColumn(0,"JANG99"));		// 공동활용본장
	dsBrncSalesTmp.SetColumn(3,"JANG19",dsBrncSales.GetColumn(0,"GYEJWA"));		// 그린카드
	dsBrncSalesTmp.SetColumn(3,"JANG20",dsBrncSales.GetColumn(0,"GYOCHA"));		// 교차

	var sJang99Nm = "미사리";
	if (dsRaceInfo.GetColumn(0, "MEET_CD") == "003") sJang99Nm = "광명";
	dsBrncSalesTmp.SetColumn(2,"JANG18",sJang99Nm);		// 공동활용본장

