}

/*-------------------------------------------------------------------------------------------------+
>>  Á¶È¸ ¹öÆ° Å¬¸¯ ãÁ Ã³¸®
+-------------------------------------------------------------------------------------------------*/
function fcSearchTms(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rsm2050-service:searchTms"; // ÃÖ±Ù °æÁÖ °á°ú ³¯Â¥
    var sInDataSet   = "";
    var sOutDataSet  = "dsTms=dsTms dsMaxTms=dsMaxTms";

	dsTms.ClearData();
	fcd_AddParam("STND_YEAR", cboStndYear.Value);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}


/*-------------------------------------------------------------------------------------------------+
>>  Á¶È¸ Á¤º¸
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rsm2050-service:searchBrncCompareSales";
	var sInDataSet   = "";
    var sBeforeStndYear = "";
	var sBeforeTms = "";	

    var sOutDataSet  = "dsBrncCompareSales=dsBrncCompareSales ";
        sOutDataSet += "dsBrncRate=dsBrncRate ";    
        sOutDataSet += "dsDayOfWeekAmt=dsDayOfWeekAmt ";    
        sOutDataSet += "dsEnterance=dsEnterance ";    
        sOutDataSet += "dsTotalAmt=dsTotalAmt ";    
        sOutDataSet += "dsAMCross=dsAMCross ";
        sOutDataSet += "dsTotalYear=dsTotalYear ";
        sOutDataSet += "dsTotalTms=dsTotalTms ";
        sOutDataSet += "dsTotalTms2=dsTotalTms2 ";
        sOutDataSet += "dsSaleGreenDiv=dsSaleGreenDiv ";

	fcd_AddParam("STND_YEAR", cboStndYear.Value);
	fcd_AddParam("B_STND_YEAR", (parseInt(cboStndYear.Value)-1));
	fcd_AddParam("TMS", cboTms.Value);
	if (cboTms.Value == "1") {	// Àü³âµµ ¸¶Áö¸·È¸Â÷¸¦ ÀüÈ¸Â÷·Î Ã³¸®
		var nRow = -1;
		nRow = dsMaxTms.findRow("STND_YEAR", ToString(parseInt(cboStndYear.Value)-1));
		sBeforeTms = dsMaxTms.GetColumn(nRow, "MAX_TMS");	
		sBeforeStndYear = ToString(parseInt(cboStndYear.Value)-1);
	} else {
		sBeforeTms = ToString(parseInt(cboTms.Value)-1);
		sBeforeStndYear = cboStndYear.Value;
	}
	fcd_AddParam("B_TMS_YEAR", sBeforeStndYear);
	fcd_AddParam("B_TMS", sBeforeTms);

	stSalesTitle.Text = "¸ÅÃâÇöÈ² "+cboStndYear.Value+"³â "+cboTms.Value+"È¸Â÷";
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}


// ÄÞº¸¹Ú½º º¯°æ ÀÌº¥Æ®
function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	switch ( obj.ID ) {

		case "cboStndYear" : // ±âÁØ³âµµ º¯°æ
			fcSearchTms();
			break;

		case "cboTms" :		// È¸Â÷ º¯°æ
			fcSearch();
			break;

	}


}



