/*-------------------------------------------------------------------------------------------------+
>>  Transaction ÈÄ Ã³¸® ÇØ¾ß ÇÒ ³»¿ë!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

    if ( sServiceName == "rsm2050-service:searchTms" ) { // TMS °ª °Ë»ö

		if(dsTms.GetRowCount()==0)
		{		
			dsTms.SetColumn(0,"TMS","");
			dsTms.SetColumn(0,"TMS_DESC","¾øÀ½");
			cboTms.Index=0;
		}
		else
		{
			cboTms.Value = dsTms.GetColumn(0,"MAX_TMS");		
			fcSearch();
		}
	}


	if ( sServiceName == "rsm2050-service:searchBrncCompareSales" ) 
	{
		fcTotalYearTmp();// ·¹Æ÷Æ®¿ë µ¥ÀÌÅ¸¼Â »ý¼º
		stSalesTitle.Text = "¸ÅÃâÇöÈ² "+cboStndYear.Value+"³â "+cboTms.Value+"È¸Â÷";
		stAMCross.Text = "*±³Â÷ÅõÇ¥ ¼ö¼ö·á¼öÀÔ(¸ÅÃâ¾×ÀÇ 7.5%): "+dsAMCross.GetColumn(0,"N_AMT_COMMI")+"¹é¸¸¿ø(ÀüÈ¸:"+dsAMCross.GetColumn(0,"B_AMT_COMMI")+"¹é¸¸¿ø)";
		grdBrncCompareSales.SetFocus();
		dsBrncCompareSales.row = -1;
		dsSaleGreenDiv.row = -1;
		dsDayOfWeekAmt.row = -1;
		if (dsBrncCompareSales.rowcount()>21) grdBrncCompareSales.Height = 477;
		else                                  grdBrncCompareSales.Height = 477 - grdBrncCompareSales.RowHeight;
		if (dsSaleGreenDiv.rowcount() > 6) grdSaleGreenDiv.Height=208;
		else                               grdSaleGreenDiv.Height=208-grdSaleGreenDiv.RowHeight;
		return;
	}

}

