function fcToExcel(obj) {

	var sExcelList = "grdBrncCompareSales:5"; 	
		sExcelList += "|grdSaleGreenDiv:5"; 
		sExcelList += "|grdBrncRate:2"; 
		sExcelList += "|grdDayOfWeekAmt:3"; // :숫자는 + head +summary+ 하단 띄우고 싶은 사이 간격
		sExcelList += "|grdEnterance:3"; // :숫자는 + head +summary+ 하단 띄우고 싶은 사이 간격
		sExcelList += "|grdTotalAmt:2"; // :숫자는 + head +summary+ 하단 띄우고 싶은 사이 간격

    fnc_OpenMultiExcelExport(sExcelList,"리포트경정");


}

