/*-------------------------------------------------------------------------------------------------+
>>  인쇄
+-------------------------------------------------------------------------------------------------*/
function fcPrint(obj)
{
	/* 인쇄 시, 매출순으로 정렬(그린카드,광복,서면 제외) */ 
	dsBranchAmt1.ClearData();
	dsBranchAmt1.Copy(dsBranchAmt);

	dsBrncCompareSales.ClearData();
	dsBrncCompareSales.Copy(dsBranchAmt);


	dsBranchAmt1.Sort("N_DIV_TOTAL", false);	//금회 매출액으로 Sorting

	for(var i=0;i<dsBranchAmt1.rowcount;i++) {
		dsBranchAmt1.SetColumn(i, "ORD", i+1);
	}

	dsBranchAmt1.SetColumn(dsBranchAmt1.FindRow("TDIV_NO", "06") , "ORD", 100);
	dsBranchAmt1.SetColumn(dsBranchAmt1.FindRow("TDIV_NO", "071"), "ORD", 101);
	dsBranchAmt1.SetColumn(dsBranchAmt1.FindRow("TDIV_NO", "072"), "ORD", 102);
	dsBranchAmt1.SetColumn(dsBranchAmt1.FindRow("TDIV_NO", "073"), "ORD", 103);
	dsBranchAmt1.SetColumn(dsBranchAmt1.FindRow("TDIV_NO", "074"), "ORD", 104);

	dsBranchAmt1.Sort("ORD", true);


	dsDayOfWeekAmt1.Copy(dsDayOfWeekAmt);

	//제목에 연도회차 데이터셋 
	dsStndYearTms.SetColumn(0,"STND_YEAR", cboStndYear.Value );
	dsStndYearTms.SetColumn(0,"TMS", cboTms.Value );
	dsStndYearTms.SetColumn(0,"RACE_STR_DAY", dsTms.LookUp("TMS", cboTms.Value,"RACE_STR_DAY") );
	dsStndYearTms.SetColumn(0,"RACE_END_DAY", dsTms.LookUp("TMS", cboTms.Value,"RACE_END_DAY") );


	//우측비고란의 점유율 계산
	var cnt = 0;
	for( var i=0; i<dsBranchAmt.RowCount(); i++)
	{

		if( length( toString(toInteger(dsBranchAmt.GetColumn(i, "TDIV_NO")))) > 1 && dsBranchAmt.GetColumn(i, "TDIV_NO") != "071" && dsBranchAmt.GetColumn(i, "TDIV_NO") != "072")
		{
			cnt = toFloat(cnt) + toFloat(dsBranchAmt.GetColumn(i,"DIV_TOTAL_RATE")) ;

		}
	}

	//Report사용 Img 경로
	dsReportImg.ClearData();
	dsReportImg.InsertRow(0); 
	dsReportImg.SetColumn(0, "IMG_URL", GBL_REPORT_URL + "img/ExcelImg.jpg");
	/*
	dsBranchAmtRmk.SetColumn(0, "DIV_RATE", dsBranchAmt.GetColumn(dsBranchAmt.FindRow("TDIV_NO", '01'),"DIV_TOTAL_RATE"));
	dsBranchAmtRmk.SetColumn(1, "DIV_RATE", cnt);
	dsBranchAmtRmk.SetColumn(2, "DIV_RATE", dsBranchAmt.GetColumn(dsBranchAmt.FindRow("TDIV_NO", '071'),"DIV_TOTAL_RATE"));
	dsBranchAmtRmk.SetColumn(3, "DIV_RATE", dsBranchAmt.GetColumn(dsBranchAmt.FindRow("TDIV_NO", '072'),"DIV_TOTAL_RATE"));
	*/
	dsBranchAmtRmk.SetColumn(0, "DIV_RATE", replace(dsBrncRate.GetColumn(0, "CRA_RATE"), "%", ""));
	dsBranchAmtRmk.SetColumn(1, "DIV_RATE", replace(dsBrncRate.GetColumn(0, "BRN_RATE"), "%", ""));
	dsBranchAmtRmk.SetColumn(2, "DIV_RATE", replace(dsBrncRate.GetColumn(0, "CHANG_RATE"), "%", ""));
	dsBranchAmtRmk.SetColumn(3, "DIV_RATE", replace(dsBrncRate.GetColumn(0, "BUSAN_RATE"), "%", ""));

	dsEnterance1.SetColumn(0, "N_IN_INWON_ENT"     , round(dsEnterance.GetColumn(dsEnterance.FindRow("BRANCH_CD", "000"), "N_IN_INWON") / 1000));
	dsEnterance1.SetColumn(0, "L_IN_INWON_ENT"     , round(dsEnterance.GetColumn(dsEnterance.FindRow("BRANCH_CD", "000"), "L_IN_INWON") / 1000));
	dsEnterance1.SetColumn(0, "B_IN_INWON_RATE_ENT", (dsEnterance.GetColumn(dsEnterance.FindRow("BRANCH_CD", "000"), "B_IN_INWON_RATE")));
	dsEnterance1.SetColumn(0, "B_IN_INWON_ENT"     , round(dsEnterance.GetColumn(dsEnterance.FindRow("BRANCH_CD", "000"), "B_IN_INWON") / 1000));
	dsEnterance1.SetColumn(0, "N_IN_INWON_BET"     , (dsEnterance.GetColumn(dsEnterance.FindRow("BRANCH_CD", "300"), "N_IN_INWON")));
	dsEnterance1.SetColumn(0, "L_IN_INWON_BET"     , (dsEnterance.GetColumn(dsEnterance.FindRow("BRANCH_CD", "300"), "L_IN_INWON")));
	dsEnterance1.SetColumn(0, "B_IN_INWON_RATE_BET", (dsEnterance.GetColumn(dsEnterance.FindRow("BRANCH_CD", "300"), "B_IN_INWON_RATE")));
	dsEnterance1.SetColumn(0, "B_IN_INWON_BET"     , (dsEnterance.GetColumn(dsEnterance.FindRow("BRANCH_CD", "300"), "B_IN_INWON")));
	dsEnterance1.SetColumn(0, "L_IN_INWON_RATE"    , (dsEnterance.GetColumn(dsEnterance.FindRow("BRANCH_CD", "000"), "L_IN_INWON_RATE")));
	dsEnterance1.SetColumn(0, "B_IN_INWON_RATE"    , (dsEnterance.GetColumn(dsEnterance.FindRow("BRANCH_CD", "300"), "L_IN_INWON_RATE")));

	var sBettingPlus = "";
	var sSumngPlus   = "";

	if( dsEnterance.GetColumn( dsEnterance.FindRow("BRANCH_CD", "000"), "N_IN_INWON" ) >=
        dsEnterance.GetColumn( dsEnterance.FindRow("BRANCH_CD", "000"), "L_IN_INWON" ) ) {
		sSumngPlus = "증가";
    } else {
		sSumngPlus = "감소";
    }

    if( dsEnterance.GetColumn( dsEnterance.FindRow("BRANCH_CD", "300"), "N_IN_INWON" ) >=
        dsEnterance.GetColumn( dsEnterance.FindRow("BRANCH_CD", "300"), "L_IN_INWON" ) ) {
		sBettingPlus = "증가";
    } else {
		sBettingPlus = "감소";
    }

	dsEnterance1.SetColumn(0, "B_PLUS", sBettingPlus);
	dsEnterance1.SetColumn(0, "L_PLUS", sSumngPlus);

	var sFormId 	= "";
	var sArgss 		= "";
	var arrReturn   = array(2);
	vReturn = "";
	sFormId = "frm_rsm::RSM2060P.xml";
	sArgs = "";	

	arrReturn = Dialog(sFormId, sArgss, 240, 128, "");

	if (fnc_IsNull(vReturn) == false )
	{
		if ( vReturn = "1" )
		{
			dsEnterance.Sort("BRANCH_CD", true);

			//     경륜개최결과 (매x액 + 누계 + 참고자료)
			sSubUrl   = "";                                             // 레포트 파일이 있는 서브디렉토리
			sRptNm    = "rsm/rsm2060_6.jrf";                                // 레포트 파일명
/*
			sDataSet  = "dsStndYearTms=dsStndYearTms "
					  + "dsBranchAmt1=dsBranchAmt1 "
					  + "dsTotalAmt=dsTotalAmt "
					  + "dsEnterance=dsEnterance "
					  + "dsBranchAmtRmk=dsBranchAmtRmk "					//UBI 쿼리명, MIP Dataset명"
					  + "dsSaleGreenDiv=dsSaleGreenDiv ";				
*/
			sDataSet  = "dsStndYearTms=dsStndYearTms "
					  + "dsBrncCompareSales=dsBrncCompareSales "
					  + "dsTotalAmt=dsTotalAmt "
					  + "dsEnterance=dsEnterance "
					  + "dsSaleGreenDiv=dsSaleGreenDiv ";					//지점별 그린카드 매출액
			sArg      = "";		
			sRptDrct  = 2;                                              // 출력방향(0 : 수직, 1 : 수평)    
			fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
		} else if ( vReturn = "2" )
		{

			//전년대비 평균매출액 ( 매출액 + 요일별매출현황)
			/*
			sSubUrl   = "";                                             // 레포트 파일이 있는 서브디렉토리
			sRptNm    = "rsm/rsm2060_3.jrf";                                // 레포트 파일명
			sDataSet  = "dsStndYearTms=dsStndYearTms "
					  + "dsBranchAmt1=dsBranchAmt1 ";					//UBI 쿼리명, MIP Dataset명"
			sArg      = "";		
			sRptDrct  = 1;  
			fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
			*/
						//데이터셋을 초기화 한다.
				ds_reportParam.DeleteAll();
				var nRnum;

				fcd_AddParam("STND_YEAR"	, cboStndYear.Value	);		// 
				fcd_AddParam("TMS"		, cboTms.Value		);		// 


				// 필드 값을 셋팅한다.
				var paramField = [
										"STND_YEAR"						// 기준년도 
									,	"TMS"							// 회차 

								 ];

				// 값을 셋팅한다.
				var paramValue = [
										cboStndYear.Value
									,	cboTms.Value
								 ];


				var rowCnt = paramField.length;	
				for(var row = 0 ; row < rowCnt; row++)
				{
					nRnum = ds_reportParam.AppendRow();
					ds_reportParam.SetColumn(nRnum, "PARAM_NM", paramField[row]);
					ds_reportParam.SetColumn(nRnum, "PARAM_VAL",paramValue[row]);
				}

				/* 레포트 배율 추가 - 시작 */
				iRow = ds_reportParam.AddRow();

				// Default Scale 파라미터명 추가(고정)
				ds_reportParam.SetColumn(iRow, "PARAM_NM", "DEFAULT_SCALE");
				ds_reportParam.SetColumn(iRow, "PARAM_VAL", "1");

				//레포트 트랙잭션을 태운다.
				fnc_ReportPreView("rsm2060_7", ds_reportParam.ID, "N");

		} else if ( vReturn = "3" )
		{
			sSubUrl   = "";                                             // 레포트 파일이 있는 서브디렉토리
			sRptNm    = "rsm/rsm2060_8.jrf";                                // 레포트 파일명
			sDataSet  = "dsTotalYearTmp=dsTotalYearTmp "
			          + "dsStndYearTms=dsStndYearTms "
			          + "dsTotalTms=dsTotalTms "
			          + "dsTotalTms2=dsTotalTms2 "
			          + "dsReportImg=dsReportImg ";					//UBI 쿼리명, MIP Dataset명"
			sArg      = "";		
			sRptDrct  = 1;  
trace("#####경정매출현황:"+sRptNm);			
			fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
		} else if ( vReturn = "4" )
		{
			//     경정개최결과  (매출액  + 요일별 매출현황)
			sSubUrl   = "";                                             // 레포트 파일이 있는 서브디렉토리
			sRptNm    = "rsm/rsm2060_4.jrf";                                // 레포트 파일명
			sDataSet  = "dsStndYearTms=dsStndYearTms "
					  + "dsBranchAmt1=dsBranchAmt1 "
					  + "dsEnterance1=dsEnterance1 "
					  + "dsDayOfWeekAmt1=dsDayOfWeekAmt1 ";					//UBI 쿼리명, MIP Dataset명"
			sArg      = "";		
			sRptDrct  = 2;  
			fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
		}
	}
}

